function fnForm_Loaded(obj)
{       
	//검색일자를 일주일전으로 초기화
	divSearch.scDATE1.Value = AddDate(Today(), -14);
	//wbjeon 2010-09-30 수정  2012-10-04 서춘교 수정	
	//var thisToDay = Today();
	//divSearch.scDATE1.Value = fromDayAfter(thisToDay,-14,0,"add");
		
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
	divButtonBar.Image1.text = space(6) + "행정처분 등록 현황";
	
	//obj = object("global.MainMenu.sub_img1");
	//obj.ImageID = "left_menu2_1_on";
	//obj.Color = "#1C3C5A";
	
	
	//최초 시도코드 검색
	fnGetCode();

	//초기화
	traceControl( this );
	if(pscMode=="T"){
		fnControlInit("pscSIDO_CODE,pscDATE1,pscDATE2,pscCOM_NAME,pscREP_NM",this);
		fnTablesBinding();
	}
	
}

