function fnGetCode()
{
	var sArgs = "";
	var sTid = "GET_SIDO_CODE";
	var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=SidoCode";
	var sInDs = "";
	var sOutDs = "dsSIDO_CODE=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);	
}

function ryycho_OnClick(obj)   //월초연초 검색 2012-09-29 서춘교
{
	if(this.divSearch.ryycho.Value="ycho") {

 divSearch.scDATE1.Value = substr(Today(),0,4)+'0101';
}
	else if(this.divSearch.ryycho.Value="yycho") {

 divSearch.scDATE1.Value = substr(Today(),0,6)+'01';
}
}
