var	_sKEY1;
var	_sKEY2;
var _sWorkFlag;

//////////////////////////////////////////////////////////////////
// Form Load 이벤트
//////////////////////////////////////////////////////////////////
function fnForm_Loaded(obj)
{
	global.gv_WorkFlag = _sWorkFlag;
	
	fnGetCodeValue();
	Combo1_OnChanged();
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnInquiry();
	}
	else if(global.gv_WorkFlag == global.gv_INSERT)
	{
		global.gv_WorkFlag = global.gv_INSERT;
		fnInitButtonBar();
	}
	else if(global.gv_WorkFlag == global.gv_MODIFY)
	{
		fnModify();
	}
	
	
	this.tbMain.tpProc.Calendar3.value = Today();	
	this.tbMain.tpProc.Calendar2.value = Today();
	this.tbMain.tpProc.Calendar4.value =Today();
	this.tbMain.tpProc.Calendar5.value =Today();
	this.tbMain.tpProc.Calendar6.value = Today();
	
	fnInitMemoGroup("INQUIRY");
	fnInitNotiGroup("INQUIRY");
	
	tbMain.MoveTab(4, 0);
		
	divButtonBar.Image1.text = space(6) + "등록취소";
}

function fnForm_Activate(obj)
{
	global.g_activeFrm = this.Id;
}

