/**
 1. 함수명 : fnInitButtonBar
 2. 기  능 : 화면상단 버튼상태 설정
 3. Params 
    없음
 4. Return
    없음
*/
function fnInitButtonBar()
{

	if (global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = true;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, false);
		divButtonBar.btnCancel.enable = false;
		divButtonBar.btnReport.enable = true;
		divButtonBar.btnExcel.enable = true;		
	}
	else if (global.gv_WorkFlag == global.gv_INSERT)
	{
		fnAllControlState(false, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnCancel.enable = true;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;		
	}
	else
	{

		fnAllControlState(true, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnCancel.enable = true;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;

		fnControlState(false, this, "RC_REGIST_NUM,COM_NAME,COI_WRT_NUM,ADDR_POST_NUM,ADDR_ADDR,ADDR_DETAIL_ADDR,NEXT_DISPO");	
		
	}
}


/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	var sArgs = "workMODE=CancelMgrC scWRT_NUM=" + _sKEY1 + " scTMP_WRT_NUM=" + _sKEY2;
	var sTid = "SEARCH";
	var sUrl = "ICCS::am/AmAction.do?cmd=CancelMgrC";
	var sInDs = "";
	var sOutDs  = "dsPT_M_REGIST_CANCEL=output dsPT_M_MASTER=output2 " ;
		sOutDs += "dsPT_M_AUDI_REPORT=output3 dsPT_M_AUDI_DICTION=output4 " ;
		sOutDs += "dsPT_M_AUDI_OPINION=output5 dsPT_M_BEF_REPORT=output6 " ;
		sOutDs += "dsPT_M_MEMO=output7 dsPT_M_AUDI_BEF_REPORT=output8 " ;
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	http.sync = false;
	
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "NOTI_SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			fnNotiButton_Click(tbMain.tpNoti.btnNotiCancel);
			fnInitNotiGroup("INQUIRY");
			return;
		}
		if (svcid == "NOTI_DELETE")
		{
			alert('데이터 삭제 중 오류가 발생하였습니다.');
			fnInitNotiGroup("INQUIRY");
			return;
		}
		if (svcid == "MEMO_SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			fnMemoButton_Click(tbMain.tpMemo.btnMemoCancel);
			fnInitMemoGroup("INQUIRY");
			return;
		}
		if (svcid == "MEMO_DELETE")
		{
			alert('데이터 삭제 중 오류가 발생하였습니다.');
			fnInitMemoGroup("INQUIRY");
			return;
		}
	}
		
	if(svcid == "SEARCH")
	{
		
		if(dsPT_M_AUDI_DICTION.rowcount  == 0)
		{
			dsPT_M_AUDI_DICTION.AddRow();
			dsPT_M_AUDI_DICTION.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));
			dsPT_M_AUDI_DICTION.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));
		}
		
		if(dsPT_M_AUDI_OPINION.rowcount == 0)
		{
			dsPT_M_AUDI_OPINION.AddRow();
			dsPT_M_AUDI_OPINION.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));
			dsPT_M_AUDI_OPINION.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));	
		}
			
		if(dsPT_M_AUDI_REPORT.rowcount ==0)
		{
			dsPT_M_AUDI_REPORT.AddRow();
			dsPT_M_AUDI_REPORT.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));	
			dsPT_M_AUDI_REPORT.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));		
		}

		/* 2010-07-19 wbjeon 수정*/
		if(dsPT_M_REGIST_CANCEL.rowcount ==0)
		{
			dsPT_M_REGIST_CANCEL.AddRow();
			dsPT_M_REGIST_CANCEL.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));	
			dsPT_M_REGIST_CANCEL.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));		
			dsPT_M_REGIST_CANCEL.SetColumn(0, "AUDI_EXEC_YN", "Y");		
		}
							
		Combo1_OnChanged();	
		
		fnInitAudiNotiGroup("INQUIRY");
		fnInitNotiGroup("INQUIRY");
		
	}
		
	if (svcid == "SAVE")
	{
		alert('저장되었습니다.');
		fnInquiry();
	}
	
	if (svcid == "INSERT_SAVE")
	{	
		global.gv_WorkFlag = global.gv_MODIFY;
		fnInitButtonBar();
		
		if (dsPT_TEMPLET.rowcount > 0)
		{
			var User_ID = global.g_User_ID;
			var Form_ID = "CancelMgrC";
			var sArgs = "User_ID=" + User_ID + " Form_ID=" + Form_ID;
			var sReturn = fnCreateDialog("st::TempletOutput.xml", sArgs);
		}
		
		dsPT_M_REGIST_CANCEL.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));
		dsPT_M_REGIST_CANCEL.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));
		
		dsPT_M_AUDI_DICTION.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));
		dsPT_M_AUDI_OPINION.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));
		dsPT_M_AUDI_REPORT.SetColumn(0, "WRT_NUM", dsPT_M_MASTER.GetColumn(0, "WRT_NUM"));
		
		dsPT_M_AUDI_DICTION.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));
		dsPT_M_AUDI_OPINION.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));
		dsPT_M_AUDI_REPORT.SetColumn(0, "TMP_WRT_NUM", dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM"));
	}
	
	if (svcid == "MEMO_SAVE")
	{
		alert('저장되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	if (svcid == "MEMO_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	
	if (svcid == "NOTI_SAVE")
	{
		alert('저장되었습니다.');
		fnInitNotiGroup("INQUIRY");
	}

	if (svcid == "NOTI_AUDI_SAVE")
	{
		alert('저장되었습니다.');
		fnInitAudiNotiGroup("INQUIRY");
	}
	
	if (svcid == "NOTI_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitNotiGroup("INQUIRY");
	}
	
	if (svcid == "NOTI_AUID_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitAudiNotiGroup("INQUIRY");
	}
	
	if (svcid == "CLOSE_CORP")
	{
		http.sync = true;
	}
	
}

