function fnInquiry()
{
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
}

/**
 1. 함수명 : fnInsert
 2. 기  능 : 신규버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/

function fnCancel()
{
	if(!Confirm('취소하시겠습니까?')) return;
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		Close();
	}
	else
	{
		fnRejectChange(this.dsPT_M_REGIST_CANCEL);
		fnRejectChange(this.dsPT_M_AUDI_REPORT);
		fnRejectChange(this.dsPT_M_AUDI_DICTION);
		fnRejectChange(this.dsPT_M_AUDI_OPINION);
		fnRejectChange(this.dsPT_M_MASTER);
		fnRejectChange(this.dsPT_M_MEMO);
		fnRejectChange(this.dsPT_M_BEF_REPORT);
		fnRejectChange(this.dsPT_M_AUDI_BEF_REPORT);
		
		alert('취소되었습니다.');
	}

	fnInquiry();
}

