/**
 1. 함수명 : fnSave
 2. 기  능 : 저장버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnSave()
{
	dsPT_M_AUDI_DICTION.SetColumn(0, "AUDI_SUBJ", dsPT_M_AUDI_REPORT.GetColumn(0, "AUDI_SUBJ"));
	
	if (dsPT_M_MASTER.GetColumn(0, "MOT_STE") == "C00002")
	{
		if(fnEmpty(dsPT_M_MASTER.GetColumn(0, "DISPO_DT")))
		{
			var sMessage = "행정처분 일자가 입력되지 않았습니다.\n\n";
				sMessage += "    처분을 완료할 수 없습니다.";
			alert(sMessage);
			dsPT_M_MASTER.SetColumn(0, "MOT_STE", "C00001");
			return;
		}
		
		if(fnEmpty(dsPT_M_MASTER.GetColumn(0, "DISPO_CAUSE")))
		{
			var sMessage = "행정처분 사유가 입력되지 않았습니다.\n\n";
				sMessage += "    처분을 완료할 수 없습니다.";
			alert(sMessage);
			dsPT_M_MASTER.SetColumn(0, "MOT_STE", "C00001");
			return;
		}
	}
	
	
	if(Confirm('공사업체에 대한 폐업처리를 진행하시겠습니까?')) 
	{
		http.sync = false;
		
		var sArgs = "workMODE=CLO002 scTMP_WRT_NUM=" + dsPT_M_MASTER.GetColumn(0, "TMP_WRT_NUM");
		var sTid = "CLOSE_CORP";
		var sUrl = "ICCS::cm/CmAction.do?cmd=CorpCloseProc";
		var sInDs = "";
		var sOutDs = "dsClose=output";
		var sCallBack = "fn_CallBack";
		 
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else
	{
		var sMessage = "폐업처리가 완료되지 않았습니다.\n\n";
			sMessage += "   처분을 완료할 수 없습니다.";
		alert(sMessage);
		dsPT_M_MASTER.SetColumn(0, "MOT_STE", "C00001");
	}

	var sArgs = "workMODE=ALL";
	var sTid = "SAVE";
	var sUrl = "ICCS::am/AmAction.do?cmd=CancelMgrCUD";
	var sInDs = "dsPT_M_MASTER=dsPT_M_MASTER:u dsPT_M_REGIST_CANCEL=dsPT_M_REGIST_CANCEL:u ";
		sInDs += "dsPT_M_AUDI_DICTION=dsPT_M_AUDI_DICTION:u dsPT_M_AUDI_OPINION=dsPT_M_AUDI_OPINION:u ";
		sInDs += "dsPT_M_AUDI_REPORT=dsPT_M_AUDI_REPORT:u";
	var sOutDs = "dsPT_M_MASTER=output dsPT_M_REGIST_CANCEL=output2 ";
		sOutDs += "dsPT_M_AUDI_DICTION=output3 dsPT_M_AUDI_OPINION=output4 dsPT_M_AUDI_REPORT=output5";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

