//////////////////////////////////////////////////////////////////
// Memo Detail Event
//////////////////////////////////////////////////////////////////
function fnMemoButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpMemo.btnMemoInsert)
	{
		var sTime = GetDate();
		
		sDtWorkFlag = global.gv_INSERT;
		dsPT_M_MEMO.AddRow();
		dsPT_M_MEMO.SetColumn(dsPT_M_MEMO.row, "WRT_NUM", _sKEY1);
		dsPT_M_MEMO.SetColumn(dsPT_M_MEMO.row, "TMP_WRT_NUM", _sKEY2);
		dsPT_M_MEMO.SetColumn(dsPT_M_MEMO.row, "SEQ", dsPT_M_MEMO.rowcount);
		dsPT_M_MEMO.SetColumn(dsPT_M_MEMO.row, "WRT_NAME", global.g_Name);
		dsPT_M_MEMO.SetColumn(dsPT_M_MEMO.row, "WRT_TIME", Mid(sTime, 8, 2) + Mid(sTime, 10, 2));
		dsPT_M_MEMO.SetColumn(dsPT_M_MEMO.row, "MEMO_DT", Today());

		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpMemo.btnMemoModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpMemo.btnMemoRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_M_MEMO.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_M_MEMO);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		
		var sArgs = "workMODE=MEMO";
		var sTid = "MEMO_DELETE";
		var sUrl = "ICCS::am/AmAction.do?cmd=CancelMgrCUD";
		var sInDs = "dsPT_M_MEMO=dsPT_M_MEMO:u";
		var sOutDs = "dsPT_M_MEMO=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpMemo.btnMemoCancel)
	{
		if (dsPT_M_MEMO.GetRowType(dsPT_M_MEMO.row) == "insert")
			dsPT_M_MEMO.DeleteRow(dsPT_M_MEMO.row);
		else
			fnRejectChange(dsPT_M_MEMO);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpMemo.btnMemoSave)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		var sArgs = "workMODE=MEMO";	
		var sTid = "MEMO_SAVE";
		var sUrl = "ICCS::am/AmAction.do?cmd=CancelMgrCUD";
		var sInDs = "dsPT_M_MEMO=dsPT_M_MEMO:u";
		var sOutDs = "dsPT_M_MEMO=output";
		var sCallBack = "fn_CallBack";
		
