function fnNotiButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpProc.btnNotiInsert)
	{
		sDtWorkFlag = global.gv_INSERT;
		dsPT_M_BEF_REPORT.AddRow();
		dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.row, "WRT_NUM", _sKEY1);
		dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.row, "TMP_WRT_NUM", _sKEY2);
		dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.row, "SEQ", dsPT_M_BEF_REPORT.rowcount);
		fnInitNotiGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpProc.btnNotiModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitNotiGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpProc.btnNotiRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_M_BEF_REPORT.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_M_BEF_REPORT);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		
		var sArgs = "workMODE=NOTI";
		var sTid = "NOTI_DELETE";
		var sUrl = "ICCS::am/AmAction.do?cmd=CancelMgrCUD";
		var sInDs = "dsPT_M_BEF_REPORT=dsPT_M_BEF_REPORT:u";
		var sOutDs = "dsPT_M_BEF_REPORT=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpProc.btnNotiCancel)
	{
		if (dsPT_M_BEF_REPORT.GetRowType(dsPT_M_BEF_REPORT.row) == "insert")
			dsPT_M_BEF_REPORT.DeleteRow(dsPT_M_BEF_REPORT.row);
		else
			fnRejectChange(dsPT_M_BEF_REPORT);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitNotiGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpProc.btnNotiSave)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		var sArgs = "workMODE=NOTI";	
		var sTid = "NOTI_SAVE";
		var sUrl = "ICCS::am/AmAction.do?cmd=CancelMgrCUD";
		var sInDs = "dsPT_M_BEF_REPORT=dsPT_M_BEF_REPORT:u";
		var sOutDs = "dsPT_M_BEF_REPORT=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

