function Combo1_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

		if(Combo1.Value =='C00001')
	{
		Combo1.Color ='red';	
	}
	
	else if(Combo1.Value =='C00002')
	{
		Combo1.Color ='blue';	
	}
}

