function tbMain_tpProc_GUBUN_CLASS_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.rowposition,"TMP_GUBUN",this.tbMain.tpProc.GUBUN.Text);
}

function tbMain_tpProc_NOTE_METHOD_CLASS_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.rowposition,"TMP_NOTE_METHOD",this.tbMain.tpProc.NOTE_METHOD_CLASS.Text); 
}

function tbMain_tpProc_RECEIVE_YN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{	
	dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.rowposition,"TMP_RECV_YN",this.tbMain.tpProc.RECEIVE_YN.Text); 
	if(this.tbMain.tpProc.RECEIVE_YN.Value =='SEND_D'){
		fnControlState(true, this.tbMain.tpProc, "RECV_LOC");
	} else {
		fnControlState(false, this.tbMain.tpProc, "RECV_LOC");
	}
}

function tbMain_tpProc_AUDI_EXEC_YN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strText == "N"){
		this.tbMain.tpProc.AUDI_NONEXEC_CAUSE.Enable = true;
	} else {
		this.tbMain.tpProc.AUDI_NONEXEC_CAUSE.Enable = false;
		this.tbMain.tpProc.AUDI_NONEXEC_CAUSE.value = "";
	}	
}

