/**
 1. 함수명 : fnGridDouble_Click
 2. 기  능 : Grid Cell 더블크릭 이벤트 핸들러
 3. Params 
    @obj : 이벤트 오브젝트
    @nRow : 선택된 Row Index
    @nCell : 선택된 Cell Index
    @nX
    @nY
    @nPivotIndex
 4. Return
    없음
*/
function fnGridDouble_Click(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var scSIDO_CODE = dsPT_C_RES_IMAGE_SYSTEM.GetColumn(nRow,"SIDO_CODE");
	var sArgs = "scSIDO_CODE="+scSIDO_CODE;
	
	gfnCreateNewWindow("c_res::RegImageSysMgr", "./RegImageSysMgr.xml", sArgs);
}
