//////////////////////////////////////////////////////////////////
// Form 이벤트
//////////////////////////////////////////////////////////////////
function fnForm_Loaded(obj)
{
	
	
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
	divButtonBar.Image1.text = space(6) + "트래픽(행정)관리";
	fnTablesBinding();
}

function fnForm_Activate(obj)
{
	global.g_activeFrm = this.Id;
}

//////////////////////////////////////////////////////////////////
// 공통기능
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnInitButtonBar
 2. 기  능 : 화면상단 버튼상태 설정
 3. Params 
    없음
 4. Return
    없음
*/
function fnInitButtonBar()
{
	fnAllControlState(false, this);
	
	if (global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, this);
		fnControlState(true, this, "grdList");
		
		divButtonBar.btnInsert.enable = true;
		divButtonBar.btnModify.enable = true;
		divButtonBar.btnRemove.enable = true;
		
		divButtonBar.btnSave.enable = false;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = true;
	}
	else if (global.gv_WorkFlag == global.gv_INSERT)
	{
		fnAllControlState(true, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
				
	}
	else
	{
		fnAllControlState(false, this);
		fnControlState(true, this, "grdList");
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = false;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
	}
	
}

/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	var sArgs = base_code+" scSIDO_CODE="+scSIDO_CODE;
	
	var sTid = "SEARCH";
	var sUrl = "ICCS::c_res/C_resAction.do?cmd=RegTrafficadminMgr";
	var sInDs = "";
	var sOutDs = "dsPT_C_RES_TRAFFIC_ADMIN=output ds_SIGUNGU=output2";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
		}
		if (svcid == "DELETE")
		{
			alert('데이터 삭제 중 오류가 발생하였습니다.');
		}
		return;
	}
	
	if (svcid == "SAVE")
	{
		alert('저장되었습니다.');
		global.gv_WorkFlag = global.gv_INQUIRY;
		fnInitButtonBar();
		return;
	}
	
	if (svcid == "DELETE")
	{
		alert('삭제되었습니다.');
		return;
	}

	
	if (svcid == "SEARCH")
	{
		global.gv_WorkFlag = global.gv_INQUIRY;
		
		if(scSIDO_CODE != global.g_Sd_cd)
		{
			global.gv_WorkFlag = "";
		}
		
		fnInitButtonBar();
		return;
	}
		
}


//////////////////////////////////////////////////////////////////
// 툴바 버튼 이벤트
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnToolBarButton_Click
 2. 기  능 : 툴바버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnToolBarButton_Click(obj)
{
	if (obj == divButtonBar.btnInsert) fnInsert();
	else if (obj == divButtonBar.btnModify) fnModify();
	else if (obj == divButtonBar.btnRemove) fnRemove();
	else if (obj == divButtonBar.btnSave) fnSave();
	else if (obj == divButtonBar.btnReport) fnReport();
	else if (obj == divButtonBar.btnExcel) fnExcel();
	else if (obj == divButtonBar.btnCancel) fnCancel();
	else alert('개발자오류');
}

//////////////////////////////////////////////////////////////////
// 툴바 버튼 이벤트 핸들러
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnInsert
 2. 기  능 : 신규버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnInsert()
{
	global.gv_WorkFlag = global.gv_INSERT;
	fnInitButtonBar();
	dsPT_C_RES_TRAFFIC_ADMIN.AddRow();
}

/**
 1. 함수명 : fnModify
 2. 기  능 : 수정버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnModify()
{
	if (dsPT_C_RES_TRAFFIC_ADMIN.GetRowPosition() < 0)
	{
		alert('수정할 내용을 선택하세요.');
		return;
	}
	
	global.gv_WorkFlag = global.gv_INSERT;
	fnInitButtonBar();
}

/**
 1. 함수명 : fnRemove
 2. 기  능 : 삭제버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/

function fnRemove()
{
	if(!Confirm('선택하신 내용을 삭제하시겠습니까?')) return;
	
	var reck = fnGridRemoveChk(dsPT_C_RES_TRAFFIC_ADMIN);
	
	if(reck > 0){
		alert("삭제 권한이 없습니다.");
		return;	
	}
	
	dsPT_C_RES_TRAFFIC_ADMIN.ApplyChange();
	var nCount = fnGridRowRemove(dsPT_C_RES_TRAFFIC_ADMIN);
	
	if(nCount == 0)
	{
		alert('삭제할 데이터를 선택하세요.');
		return;
	}

	var sArgs = "workMODE=ALL"+base_code;
	var sTid = "DELETE";
	var sUrl = "ICCS::c_res/C_resAction.do?cmd=RegTrafficadminMgrCUD";
	var sInDs = "dsPT_C_RES_TRAFFIC_ADMIN=dsPT_C_RES_TRAFFIC_ADMIN:u";
	var sOutDs = "dsPT_C_RES_TRAFFIC_ADMIN=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}


/**
 1. 함수명 : fnSave
 2. 기  능 : 저장버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnSave()
{
	var sArgs = "workMODE=ALL"+base_code;
	var sTid = "SAVE";
	var sUrl = "ICCS::c_res/C_resAction.do?cmd=RegTrafficadminMgrCUD";
	var sInDs = "dsPT_C_RES_TRAFFIC_ADMIN=dsPT_C_RES_TRAFFIC_ADMIN:u";
	var sOutDs = "dsPT_C_RES_TRAFFIC_ADMIN=output";
	var sCallBack = "fn_CallBack";
	
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fnCancel()
{
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
}

