/**
 1. 함수명 : fnInitButtonBar
 2. 기  능 : 화면상단 버튼상태 설정
 3. Params 
    없음
 4. Return
    없음
*/
function fnInitButtonBar()
{
	if (global.gv_WorkFlag == global.gv_INQUIRY)
	{
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = true;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(3, false);
		divButtonBar.btnCancel.enable = false;
		divButtonBar.btnReport.enable = true;
		divButtonBar.btnExcel.enable = true;		
	}
	else if (global.gv_WorkFlag == global.gv_INSERT)
	{
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnCancel.enable = true;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
		
		fnAllControlState(false, this);
		fnControlState(true, this.tbMain.tpBase, "RECV_NUM,btnCheck");
	}
	else if(global.gv_WorkFlag == global.gv_MODIFY)
	{
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnCancel.enable = true;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
		
		fnAllControlState(true, this.tbMain.tpBase);
		fnAllControlState(true, this.tbMain.tpPaper);
		fnControlState(false, this.tbMain.tpBase, "RECV_NUM,btnCheck");
	}	
}

/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	var sArgs = "workMODE=ALL RECV_NUM=" + quote(_sKEY1) + " scSIDO_CODE=" + global.g_Sd_cd;
	var sTid = "SEARCH";
	var sUrl = "ICCS::cm/CmAction.do?cmd=ChgReceiptMgrC";
	var sInDs = "";
	var sOutDs = "dsPT_R_CHANGE_STATEMENT=output dsPT_R_MEMO=output2 dsPT_R_SUBSIDIARY_COMPANY=output3 dsPT_R_DEFICIT=output4 dsPT_R_COMPANY_MASTER=output5" ;
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	http.sync = false;
	
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
	}
	
	if (svcid == "CHECK_KEY")
	{
		if (dsCheckKey.rowcount > 0)
		{
			alert('존재하는 민원접수번호입니다.');
			tbMain.tpBase.RECV_NUM.Text = "";
			tbMain.tpBase.RECV_NUM.SetFocus();
		}
		else
		{
		
		   
			dsPT_R_CHANGE_STATEMENT.SetColumn(0, "INS_DT", strDate);
			dsPT_R_CHANGE_STATEMENT.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
			dsPT_R_CHANGE_STATEMENT.SetColumn(0, "WRT_ID", global.g_User_ID);
			
			var sArgs = "workMODE=MAIN";	
			var sTid = "INSERT_SAVE";
			var sUrl = "ICCS::cm/CmAction.do?cmd=ChgReceiptMgrCCUD";
			var sInDs = "dsPT_R_CHANGE_STATEMENT=dsPT_R_CHANGE_STATEMENT:u";
			var sOutDs = "dsPT_R_CHANGE_STATEMENT=output";
			var sCallBack = "fn_CallBack";
			
			Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		}
	}
	
	if (svcid == "GET_CORP")
	{
		if (dsPT_R_COMPANY_MASTER.rowcount == 0)
		{
			alert('잘못된 공사업등록번호 입니다.');
			return;
		}
		
		//fnSetMasterDetail("dsPT_R_COMPANY_MASTER", "dsPT_R_CHANGE_STATEMENT");
		
		for(var nLoop = 0; nLoop < dsPT_R_SUBSIDIARY.rowcount; nLoop++)
		{
			dsPT_R_SUBSIDIARY_COMPANY.AddRow();
			dsPT_R_SUBSIDIARY_COMPANY.SetColumn(nLoop, "OTOB_COM_NUM",
												dsPT_R_SUBSIDIARY.GetColumn(0, "SUB_WRT_NUM"));
			dsPT_R_SUBSIDIARY_COMPANY.SetColumn(nLoop, "SIDO_CODE", global.g_Sd_cd);
			dsPT_R_SUBSIDIARY_COMPANY.SetColumn(nLoop, "OTOB_COM_CODE",
												dsPT_R_SUBSIDIARY.GetColumn(0, "SUB_CODE"));
			dsPT_R_SUBSIDIARY_COMPANY.SetColumn(nLoop, "OTOB_COM_TOB_CONT",
												dsPT_R_SUBSIDIARY.GetColumn(0, "SUB_NAME"));
			dsPT_R_SUBSIDIARY_COMPANY.SetColumn(nLoop, "RECV_NUM", 
												dsPT_R_CHANGE_STATEMENT.GetColumn(0, "RECV_NUM"));
			dsPT_R_SUBSIDIARY_COMPANY.SetColumn(nLoop, "SEQ", nLoop + 1);
		}
	}
	
	if (svcid == "SAVE")
	{
		alert('저장되었습니다.');
		fnInquiry();
	}
	
	if (svcid == "INSERT_SAVE")
	{
		global.gv_WorkFlag = global.gv_MODIFY;
		fnInitButtonBar();
		
		var sGroup;
		var nIndex = 0;
		
		for(var nLoop = 0; nLoop < dsPT_R_DEFICIT_CODE.rowcount; nLoop++)
		{
			sGroup = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_GROUP");
			
			if (sGroup == "GUBU")
			{
				dsPT_R_DEFICIT.AddRow();
				dsPT_R_DEFICIT.SetColumn(nIndex, "RECV_NUM", tbmain.tpBase.RECV_NUM.Text);
				dsPT_R_DEFICIT.SetColumn(nIndex, "SIDO_CODE", global.g_Sd_cd);
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_SEQ", nIndex + 1);
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_CODE", dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_CODE"));
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_CONT", dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "CONT"));
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_GROUP", "GUBU");
				
				nIndex++;
			}
		}

		var sArgs = "workMODE=ALL";
		var sTid = "SET_CODE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=ChgReceiptMgrCCUD";
		var sInDs = "dsPT_R_DEFICIT=dsPT_R_DEFICIT:u";
		var sOutDs = "dsPT_R_DEFICIT=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	
	if (svcid == "COMPANY_SAVE")
	{
		alert('저장되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	if (svcid == "COMPANY_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	
	if (svcid == "MEMO_SAVE")
	{
		alert('저장되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	if (svcid == "MEMO_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	
	if (svcid == "SET_CODE")
	{
		dsPT_R_DEFICIT.ApplyChange();
	}
	
	if (svcid == "GET_CODE")
	{
		dsPT_R_CHANGE_STATEMENT.AddRow();
		dsPT_R_CHANGE_STATEMENT.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
		tbMain.tpBase.RECV_DT.Value = Today();
		tbMain.tpBase.PROC_LIM.Value = AddDate(Today(), 14);
		tbMain.tpBase.REC_INS_DT = Today();
	}
}

