function fnValidation()
{
	var nIndex = 0;
	dsError.DeleteAll();
	
	if(DateTime(tbMain.tpBase.RECV_DT.Value) > DateTime(tbMain.tpBase.PROC_LIM.Value))
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "PROC_LIM");
		dsError.SetColumn(nIndex, "TEXT", "민원처리일자가 민원접수일자보다 작습니다.");
		
		nIndex++;
	}
	
	if((DateTime(tbMain.tpBase.PROC_LIM.Value) - DateTime(tbMain.tpBase.RECV_DT.Value)) > 14)
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "PROC_LIM");
		dsError.SetColumn(nIndex, "TEXT", "민원처리일자가 14일을 초과합니다.");
		
		nIndex++;
	}
	
	if(fnEmpty(dsPT_R_CHANGE_STATEMENT.GetColumn(dsPT_R_CHANGE_STATEMENT.row, "TMP_WRT_NUM")))
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "grdPaper");
		dsError.SetColumn(nIndex, "TEXT", "기준신고 대상 업체를 선택하세요.");
		
		nIndex++;
	}

	//var nErrCnt = 0;
	for(var nLoop = 0; nLoop < dsPT_R_DEFICIT.rowcount; nLoop++)
	{
		if(dsPT_R_DEFICIT.GetColumn(nLoop, "DEFI_OUT") != "1")
		{
			//nErrCnt++;
			dsError.AddRow();
			dsError.SetColumn(nIndex, "FLD_NAME", "grdPaper");
			dsError.SetColumn(nIndex, "TEXT", "흠결이 완료되지 않았습니다.");
			nIndex++;
			break;
		}
	}
	
	if (nIndex == 0) dsPT_R_CHANGE_STATEMENT.SetColumn(0, "DEFI_STE", "O");
	else dsPT_R_CHANGE_STATEMENT.SetColumn(0, "DEFI_STE", "X");
	
	if (nIndex > 0)
	{
		var nRet = fnCreateDialog("./frmErrorDLG.xml", "");
		if (nRet < 0) return true;
		else 
		{
			fnFindComponent(this, dsError.GetColumn(nRet, "FLD_NAME"));
			return false;
		}
	}
	
	return true;
}


