function fnCompanyDetailButton_Click(obj)
{
	var sDtWorkFlag;

	if(obj == tbMain.tpBase.btnCInsert)
	{
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_SUBSIDIARY_COMPANY.AddRow();
		dsPT_R_SUBSIDIARY_COMPANY.SetColumn(dsPT_R_SUBSIDIARY_COMPANY.row, 
											"SEQ", dsPT_R_SUBSIDIARY_COMPANY.rowcount);
		dsPT_R_SUBSIDIARY_COMPANY.SetColumn(dsPT_R_SUBSIDIARY_COMPANY.row, 
											"RECV_NUM", tbMain.tpBase.RECV_NUM.Text);	
		dsPT_R_SUBSIDIARY_COMPANY.SetColumn(dsPT_R_SUBSIDIARY_COMPANY.row, "SIDO_CODE", global.g_Sd_cd);
											
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpBase.btnCModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpBase.btnCRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_SUBSIDIARY_COMPANY.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_SUBSIDIARY_COMPANY);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=COMPANY";	
		var sTid = "COMPANY_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=ChgReceiptMgrCCUD";
		var sInDs = "dsPT_R_SUBSIDIARY_COMPANY=dsPT_R_SUBSIDIARY_COMPANY:u";
		var sOutDs = "dsPT_R_SUBSIDIARY_COMPANY=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpBase.btnCCancel)
	{
		if (dsPT_R_SUBSIDIARY_COMPANY.GetRowType(dsPT_R_SUBSIDIARY_COMPANY.row) == "insert")
			dsPT_R_SUBSIDIARY_COMPANY.DeleteRow(dsPT_R_SUBSIDIARY_COMPANY.row);
		else
			fnRejectChange(dsPT_R_SUBSIDIARY_COMPANY);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitCompanyGroup(sDtWorkFlag);		
	}
	else if (obj == tbMain.tpBase.btnCSave)
	{
		var sArgs = "workMODE=COMPANY";	
		var sTid = "COMPANY_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=ChgReceiptMgrCCUD";
		var sInDs = "dsPT_R_SUBSIDIARY_COMPANY=dsPT_R_SUBSIDIARY_COMPANY:u";
		var sOutDs = "dsPT_R_SUBSIDIARY_COMPANY=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	
}


