		{
			alert('접수번호를 입력하세요.');
			return;
		}
		
		dsCheckKey.DeleteAll();
			
		var sArgs = "workMODE=CHECK RECV_NUM=" + quote(tbMain.tpBase.RECV_NUM.Text) + " scSIDO_CODE=" + global.g_Sd_cd;;
		var sTid = "CHECK_KEY";
