		var sInDs = "";
		var sOutDs = "dsCheckKey=output";
		var sCallBack = "fn_CallBack";
			
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnSetMasterDetail(dsMaster, dsDetail)
{
	var sDColName;
	var sMColName;
	var sValue;
	
	var nMCount = object(dsMaster).colcount;
	var nDCount = object(dsDetail).colcount;
	
	if(object(dsDetail).rowcount == 0)
		object(dsDetail).addrow();
	
	for(var nLoop = 0; nLoop < nDCount; nLoop++)
	{
		sDColName = object(dsDetail).GetColID(nLoop);
		for(var nIndex = 0; nIndex < nMCount; nIndex++)
		{
			sMColName = object(dsMaster).GetColID(nIndex);

			if(trim(sDColName) == trim(sMColName))
			{
				sValue = object(dsMaster).GetColumn(0, sMColName);
				object(dsDetail).SetColumn(0, sDColName, sValue);
				break;
			}
		}
	}
}

function fnLocalGridHead_Click(obj,nCell,nX,nY,nPivotIndex)
{
	var objDs = obj.BindDataset;

	if (object(objDs).RowCount() == 0)
		return;
	 
	if (obj.GetCellProp("head", nCell, "edit") == "checkbox")
	{
		if(obj.GetCellProp("head", nCell, "text") == "1")
		{
			obj.SetCellProp("head", nCell, "text", "0");
			fnSetGridCheck(0, objDs, "DEFI_OUT");
		}
		else
		{
			obj.SetCellProp("head", nCell, "text", "1");
			fnSetGridCheck(1, objDs, "DEFI_OUT");
		}
		
		//object(objDs).ApplyChange();
	}
	else
	{
		for(var nLoop = 0; nLoop < obj.GetColCount(); nLoop++)
		{
			//alert(nLoop + '===' + nCell);
			if(nLoop == nCell) continue;
			
			obj.SetCellProp("head", nLoop, "text", replace(obj.GetCellProp("head", nLoop, "text"), "▼", ""));
			obj.SetCellProp("head", nLoop, "text", replace(obj.GetCellProp("head", nLoop, "text"), "▲", ""));
		}
		
		var sHeadName = obj.GetCellProp("head", nCell, "text");
		var sColName = obj.GetCellProp("body", nCell, "colid");
		
		if (right(sHeadName, 1) == "▼") 
		{
			object(objDs).Sort(sColName, true);
			obj.SetCellProp("head", nCell, "text", replace(sHeadName, "▼", "▲"));
		}
		else if (right(sHeadName, 1) == "▲") 
		{
			object(objDs).Sort(sColName, false);
			obj.SetCellProp("head", nCell, "text", replace(sHeadName, "▲", "▼"));
		}
		else
		{
			object(objDs).Sort(sColName, true);
			obj.SetCellProp("head", nCell, "text", sHeadName + "▼");
		}
	}
}
function StdReceiptMgrC_OnDeactivate(obj)
{
	if (global.gv_WorkFlag == global.gv_INSERT || global.gv_WorkFlag == global.gv_MODIFY)
	{
		if(this.dsPT_R_CHANGE_STATEMENT.GetUpdate())
		{
			if(!Confirm('입력중인 데이터가 저장되지 않았습니다.\n\n      저장하시겠습니까?')) return;
			http.sync = true;
			fnSave();
		}
	}	
}
