function fnInitButtonBar()
{
	divButtonBar.btnInsert.enable = false;
	divButtonBar.btnModify.enable = false;
	divButtonBar.btnRemove.enable = false;
	
	divButtonBar.btnSave.enable = false;
	divButtonBar.btnCancel.enable = false;
	divButtonBar.btnReport.enable = true;	//출력만 가능
	divButtonBar.btnExcel.enable = false;
	
}

//////////////////////////////////////////////////////////////////
// 툴바 버튼 이벤트
//////////////////////////////////////////////////////////////////
function fnToolBarButton_Click(obj)
{
	if (obj == divButtonBar.btnInsert) fnInsert();
	else if (obj == divButtonBar.btnModify) fnModify();
	else if (obj == divButtonBar.btnRemove) fnRemove();
	else if (obj == divButtonBar.btnSave) fnSave();
	else if (obj == divButtonBar.btnReport) fnReport();
	else if (obj == divButtonBar.btnExcel) fnExcel();
	else if (obj == divButtonBar.btnCancel) fnCancel();
	else if (obj == divButtonBar.btnList) fnList();
	else alert('개발자오류');
}


/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	var sArgs = "workMODE=ALL scTMP_WRT_NUM=" + quote(_sKEY1) + " scRECV_NUM=" + quote(_sKEY2);
	var sTid = "SEARCH";
	var sUrl = "ICCS::cm/CmAction.do?cmd=RegChangeMgrC";
	var sInDs = "";

	var sOutDs  = " dsPT_R_COMPANY_MASTER=output1 dsPT_R_BASIC_CHANGE_HISTORY=output2 " ;
		sOutDs += " dsPT_R_CHANGE_LIST=output3 dsPT_R_WORK_RESULTS=output5 ";
		sOutDs += " dsPT_R_WORK_CAPABILITY=output6 dsPT_R_ENGINEER_CHANGE=output7 ";

	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

//두번째 탭 자동바인딩.
function fnDetailBinding()
{	
	dsPT_R_ADDRESS_CHANGE.Reset();
	dsPT_R_REPRESENT_CHANGE.Reset();
	dsPT_R_COMPANY_CHANGE.Reset();
	dsPT_R_CAPITAL_CHANGE.Reset();
	var cnt7=0, cnt8=0, cnt9=0, cnt10=0;
	for(var nLoop = 0; nLoop < dsPT_R_CHANGE_LIST.rowcount; nLoop++)
	{
		var ext = dsPT_R_CHANGE_LIST.GetColumn(nLoop,"EXT_CHG_CLASS_CODE");
		if( dsPT_R_CHANGE_LIST.GetColumn(nLoop,"STA_CHG_CLASS_CODE")=="R00001" ){//등록신고
			fn_ParseDataSet( cnt7, "A", nLoop );
			fn_ParseDataSet( cnt8, "B", nLoop );
			fn_ParseDataSet( cnt9, "C", nLoop );
			fn_ParseDataSet( cnt10, "D", nLoop );
			cnt7++; cnt8++; cnt9++; cnt10++;
		}else if( dsPT_R_CHANGE_LIST.GetColumn(nLoop,"STA_CHG_CLASS_CODE")=="R00007" ){//소재지
			fn_ParseDataSet( cnt7, "A", nLoop );
			cnt7++;
		}else if( dsPT_R_CHANGE_LIST.GetColumn(nLoop,"STA_CHG_CLASS_CODE")=="R00008" ){//자본금
			fn_ParseDataSet( cnt8, "B", nLoop );
			cnt8++;
		}else if( dsPT_R_CHANGE_LIST.GetColumn(nLoop,"STA_CHG_CLASS_CODE")=="R00009" ){//대표자
			fn_ParseDataSet( cnt9, "C", nLoop );
			cnt9++;
		}else if( dsPT_R_CHANGE_LIST.GetColumn(nLoop,"STA_CHG_CLASS_CODE")=="R00010" ){//상호
			fn_ParseDataSet( cnt10, "D", nLoop );
			cnt10++;
		}else{//합병등등
			if( IndexOf(ext,"A")>-1 ) { fn_ParseDataSet( cnt7, "A", nLoop ); cnt7++; }
			if( IndexOf(ext,"B")>-1 ) { fn_ParseDataSet( cnt8, "B", nLoop ); cnt8++; }
			if( IndexOf(ext,"C")>-1 ) { fn_ParseDataSet( cnt9, "C", nLoop ); cnt9++; }
			if( IndexOf(ext,"D")>-1 ) { fn_ParseDataSet( cnt10, "D", nLoop ); cnt10++; }
		}
		
	}
}

//fnDetailBinding 내부처리함수
function fn_ParseDataSet(row,dsType,nLoop){
	if( dsType=="A"){//소재지
		dsPT_R_ADDRESS_CHANGE.AddRow();
		dsPT_R_ADDRESS_CHANGE.SetColumn(row, "COL1", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"ADDR_ADDR")+" "+dsPT_R_CHANGE_LIST.GetColumn(nLoop,"ADDR_DETAIL_ADDR") );
		dsPT_R_ADDRESS_CHANGE.SetColumn(row, "COL2", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"OFFICE_AREA") );
		dsPT_R_ADDRESS_CHANGE.SetColumn(row, "COL3", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"ADDR_TEL_NUM")+"("+dsPT_R_CHANGE_LIST.GetColumn(nLoop,"ADDR_FAX_NUM")+")" );
		dsPT_R_ADDRESS_CHANGE.SetColumn(row, "COL4", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"DT"));
	}else if( dsType=="B"){//자본금
		dsPT_R_CAPITAL_CHANGE.AddRow();
		dsPT_R_CAPITAL_CHANGE.SetColumn(row, "COL1", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"PAY_CAP") );
		dsPT_R_CAPITAL_CHANGE.SetColumn(row, "COL2", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"REA_CAP") );
		dsPT_R_CAPITAL_CHANGE.SetColumn(row, "COL3", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"DIAG_ORG_NAME")+"("+dsPT_R_CHANGE_LIST.GetColumn(nLoop,"DIAG_NM_NM")+")" );
		dsPT_R_CAPITAL_CHANGE.SetColumn(row, "COL4", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"DT"));
	}else if( dsType=="C"){//대표자
		dsPT_R_REPRESENT_CHANGE.AddRow();
		dsPT_R_REPRESENT_CHANGE.SetColumn(row, "COL1", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"REP_NM_KOR") );
		dsPT_R_REPRESENT_CHANGE.SetColumn(row, "COL2", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"REP_NM_HAN") );
		dsPT_R_REPRESENT_CHANGE.SetColumn(row, "COL3", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"REP_SSN1")+dsPT_R_CHANGE_LIST.GetColumn(nLoop,"REP_SSN2") );
		dsPT_R_REPRESENT_CHANGE.SetColumn(row, "COL4", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"DT"));
	}else if( dsType=="D" ){
		dsPT_R_COMPANY_CHANGE.AddRow();
		dsPT_R_COMPANY_CHANGE.SetColumn(row, "COL1", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"NAME") );
		dsPT_R_COMPANY_CHANGE.SetColumn(row, "COL2", dsPT_R_CHANGE_LIST.GetColumn(nLoop,"DT"));
	}
}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	http.sync = false;
	
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "DETAIL")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
	}else{
		
		if ( svcid=="SEARCH" || svcid=="CHANGE" ){
			//세부내역 바인딩
			fnDetailBinding();
		}
	}
	
	if (svcid == "ENGINEER_SAVE")
	{
		alert('저장되었습니다.');
		fnInitEngineerGroup("INQUIRY");
		return;
	}
	if (svcid == "ENGINEER_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitEngineerGroup("INQUIRY");
		return;
	}
}

