function tbMain_tab5_goToList_OnClick(obj)
{
	var sTid = "";
	var sArgs = "";
	
	//날짜 입력 필수
	if(fnEmpty( this.tbMain.tbBUB.CHG_DT.value )){
		alert("변경일자가 입력되지 않았습니다.");
		return;
	}
	
	if(fnEmpty( this.tbMain.tbBUB.REC_DT.Value )){
		alert("접수일자가 입력되지 않았습니다.");
		return;
	}
	
	//dsPT_R_COMPANY_MASTER.SetColumn(0, "WRT_DT", Today());
	dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "WRT_DT", Today());	
	dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "RECV_NUM", "");
	
	dsPT_R_BASIC_CHANGE_HISTORY_COPY.AddRow();//새row추가후 입력처리반영
	var targetRow = 0;//신규로 추가된 행번호
	dsPT_R_BASIC_CHANGE_HISTORY_COPY.CopyRow(targetRow,"dsPT_R_BASIC_CHANGE_HISTORY",dsPT_R_BASIC_CHANGE_HISTORY.rowcount-1);
	
	dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "WRT_ID", g_User_ID);//등록자수정자
	dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "CHG_DT", this.tbMain.tbBUB.CHG_DT.value);
	dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "REC_DT", this.tbMain.tbBUB.REC_DT.value);
	
	if( obj == this.tbMain.tbBUB.companyCHG){
		if( Confirm("상호변경등록(변경일자:"+this.tbMain.tbBUB.CHG_DT.value+")을 처리하시겠습니까?") ){
			sArgs = "workMODE=COMPANY";
			sTid = "SAVE_COMPANY_NAME_CHANGE";
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "STA_CHG_CLASS_CODE", "R00010");
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "NAME", this.tbMain.tbBUB.NAME.value);
		}else{return;}
	}
	if( obj == this.tbMain.tbBUB.representCHG){
		if( Confirm("대표자변경등록(변경일자:"+this.tbMain.tbBUB.CHG_DT.value+")을 처리하시겠습니까?") ){
			sArgs = "workMODE=REPRESENT";
			sTid = "SAVE_REPRESENT_CHANGE";
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "STA_CHG_CLASS_CODE", "R00009");
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "REP_NM_KOR", this.tbMain.tbBUB.REP_NM_KOR.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "REP_NM_HAN", this.tbMain.tbBUB.REP_NM_HAN.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "REP_SSN1", this.tbMain.tbBUB.REP_SSN1.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "REP_SSN2", this.tbMain.tbBUB.REP_SSN2.value);
		}else{return;}
	}
	if( obj == this.tbMain.tbBUB.addressCHG){
		if( Confirm("소재지변경등록(변경일자:"+this.tbMain.tbBUB.CHG_DT.value+")을 처리하시겠습니까?") ){
			sArgs = "workMODE=ADDRESS";
			sTid = "SAVE_ADDRESS_CHANGE";
			//change history
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "STA_CHG_CLASS_CODE", "R00007");
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "ADDR_FAX_NUM", this.tbMain.tbBUB.ADDR_FAX_NUM.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "ADDR_TEL_NUM", this.tbMain.tbBUB.ADDR_TEL_NUM.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "ADDR_POST_NUM", this.tbMain.tbBUB.ADDR_POST_NUM.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "ADDR_ADDR", this.tbMain.tbBUB.ADDR_ADDR.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "ADDR_DETAIL_ADDR", this.tbMain.tbBUB.ADDR_DETAIL_ADDR.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "OFFICE_USE_NAME", this.tbMain.tbBUB.OFFICE_USE_CODE.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "OFFICE_AREA", this.tbMain.tbBUB.OFFICE_AREA.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "OFFICE_OWN_CLASS", this.tbMain.tbBUB.OFFICE_OWN_CLASS.value);
		}else{return;}
	}
	if( obj == this.tbMain.tbBUB.capitalCHG){
		if( Confirm("자본금변경등록(변경일자:"+this.tbMain.tbBUB.CHG_DT.value+")을 처리하시겠습니까?") ){
			sArgs = "workMODE=CAPITAL";
			sTid = "SAVE_CAPITAL_CHANGE";
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "STA_CHG_CLASS_CODE", "R00008");
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "DIAG_ORG_CODE", this.tbMain.tbBUB.DIAG_ORG_CODE.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "DIAG_NM_NM", this.tbMain.tbBUB.DIAG_NM_NM.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "DIAG_NM_WRT_NUM", this.tbMain.tbBUB.DIAG_NM_WRT_NUM.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "COMPANY_DIAG_BAS_DT", this.tbMain.tbBUB.COMPANY_DIAG_BAS_DT.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "COMPANY_DIAG_ISSUE_DT", this.tbMain.tbBUB.COMPANY_DIAG_ISSUE_DT.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "PAY_CAP", this.tbMain.tbBUB.PAY_CAP.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "REA_CAP", this.tbMain.tbBUB.REA_CAP.value);
			dsPT_R_BASIC_CHANGE_HISTORY_COPY.SetColumn(targetRow, "COMPANY_DIAG_CLASS_CODE", this.tbMain.tbBUB.COMPANY_DIAG_CLASS_CODE.value);
