//////////////////////////////////////////////////////////////////
//버튼 이벤트
//////////////////////////////////////////////////////////////////
function fnMasterButton_Click(obj)
{
	//우편번호찾기
	if (obj == this.tbMain.tbBUB.btnGetPost)
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		dsPT_R_COMPANY_MASTER.SetColumn(dsPT_R_COMPANY_MASTER.row, "ADDR_POST_NUM", sReturn[0].replace("-",""));
		dsPT_R_COMPANY_MASTER.SetColumn(dsPT_R_COMPANY_MASTER.row, "ADDR_ADDR", sReturn[1]);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "ADDR_POST_NUM", sReturn[0].replace("-",""));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "ADDR_ADDR", sReturn[1]);
	}
	
	//건물코드찾기
	if (obj == this.tbMain.tbBUB.btnGetCode)
	{
		var sArgs = "_sOldCode=BLDDIV";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;
		
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "OFFICE_USE_CODE", sReturn[0]);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "OFFICE_USE_NAME", sReturn[1]);
		dsPT_R_COMPANY_MASTER.SetColumn(0, "OFFICE_USE_CODE", sReturn[0]);
		this.tbMain.tbBUB.OFFICE_USE_CODE = sReturn[1];
	}
}

//////////////////////////////////////////////////////////////////
// Key Down 관련 이벤트
//////////////////////////////////////////////////////////////////
function fnKey_Down(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	if(nChar == "81" && bControl == true) fnTablesBinding();
	else if(nChar == "49" && bControl == true) fnInsert();
	else if(nChar == "50" && bControl == true) fnModify();
	else if(nChar == "51" && bControl == true) fnRemove();
	else if(nChar == "83" && bControl == true) fnSave();
	//else if(nChar == "55" && bControl == true) fnSave();
	else if(nChar == "88" && bControl == true) Close();
	else if(nChar == "80" && bControl == true) fnReport();
		else if(nChar == "13") 
	{
		var sCompnentname = GetCurrentComponent(true);
		if(sCompnentname.GetType() == "TextArea") return;
		GetNextComponent(true).SetFocus();
	}
	else if(nChar == "120") 
	{
		var nTabIndex = tbMain.TabIndex;
		
		if(nTabIndex == tbMain.TabCount - 1)
			tbMain.TabIndex = 0;
		else
			tbMain.TabIndex = nTabIndex + 1;
	}
}

