//////////////////////////////////////////////////////////////////
//리포팅 처리
//////////////////////////////////////////////////////////////////
function fnReport()
{
	var sCode = fnCreateDialog("st::RegReportDlg.xml", "");
	
	var sKEY1 = dsPT_R_COMPANY_MASTER.GetColumn(dsPT_R_COMPANY_MASTER.row, "TMP_WRT_NUM");

	//등록 수첩 리포트
	if (sCode == "RPT1")
	{
		var sWorkFlag = global.gv_MODIFY;
		
		var sArgs = "_sWorkFlag=" + quote(sWorkFlag) + " _sKEY1=" + quote(sKEY1);
		
		gfnCreateNewWindow("RegRePublishMgrP", "cm::RegRePublishMgrP.xml", sArgs);
	}
	// 등록증 리포트
	else if (sCode == "RPT2")
	{
		var sWorkFlag = global.gv_MODIFY;
		var sArgs = "_sWorkFlag=" + quote(sWorkFlag) + " _sKEY1=" + quote(sKEY1);
		gfnCreateNewWindow("RegReportMgrP", "cm::RegReportMgrP.xml", sArgs);
	}
	//등록대장 리포트
	else if (sCode == "RPT3")
	{
		var sViewer = "_sViewer=NewRegStR";
		var sReportname =  " _sReportname=connection.reportname=/BusinessRegist/NewRegStR.ozr";
		var sOdiNames = " _sOdiNames=odi.odinames=Q)NewRegStR";
		var	spCount = " _spCount=odi.Q)NewRegStR.pcount=1";
		var sParam = " _sParam=odi.Q)NewRegStR.args1=TMP_WRT_NUM=" + sKEY1;
		
		var sRet = fnCreateDialogResize("bas::frmReport.xml", sViewer + sReportname + sOdiNames + spCount + sParam);
	}
}

// 실적 입력 수정 삭제...
function fnCompanyDetailButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == this.tbMain.tbCap.btnResultsInsert)
	{
		sDtWorkFlag = global.gv_INSERT;
		fnInitResultsGroup(sDtWorkFlag);
		
		dsPT_R_WORK_RESULTS.AddRow();
		dsPT_R_WORK_RESULTS.SetColumn(dsPT_R_WORK_RESULTS.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_WORK_RESULTS.SetColumn(dsPT_R_WORK_CAPABILITY.row, "WRT_ID", g_User_ID);//등록자수정자
	}
	else if(obj == this.tbMain.tbCap.btnResultsModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitResultsGroup(sDtWorkFlag);
		
	}
	else if(obj == this.tbMain.tbCap.btnResultsCancel)
	{
		if (dsPT_R_REG_MEMO.GetRowType(dsPT_R_WORK_RESULTS.row) == "insert")
			dsPT_R_REG_MEMO.DeleteRow(dsPT_R_WORK_RESULTS.row);
		else
			fnRejectChange(dsPT_R_WORK_RESULTS);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitResultsGroup(sDtWorkFlag);
	}
	else if(obj == this.tbMain.tbCap.btnResultsRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_WORK_RESULTS.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_WORK_RESULTS);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		
		var sArgs = "workMODE=WORK_RESULTS";
		var sTid = "WORK_RESULTS_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=RegChangeMgrCCUD";
		var sInDs = "dsPT_R_WORK_RESULTS=dsPT_R_WORK_RESULTS:u";
		var sOutDs = "dsPT_R_WORK_RESULTS=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		
	}
	else if(obj == this.tbMain.tbCap.btnResultsSave)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitResultsGroup(sDtWorkFlag);
		
		var sArgs = "workMODE=WORK_SAVE";
		var sTid = "RESULT_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=RegChangeMgrCCUD";
		var sInDs = "dsPT_R_WORK_RESULTS=dsPT_R_WORK_RESULTS:u";
		var sOutDs = "dsPT_R_WORK_RESULTS=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		
	}
}

