//공사실적관련 버튼상태처리
function fnInitResultsGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnControlState(false, tbMain.tbCap, "year,WORK_ROM");
		
		this.tbMain.tbCap.btnResultsInsert.Enable = true;
		this.tbMain.tbCap.btnResultsModify.Enable = true;
		this.tbMain.tbCap.btnResultsRemove.Enable = true;
		this.tbMain.tbCap.btnResultsSave.Enable = false;
		this.tbMain.tbCap.btnResultsCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnControlState(true, tbMain.tbCap, "year,WORK_ROM");
		
		this.tbMain.tbCap.btnResultsInsert.Enable = false;
		this.tbMain.tbCap.btnResultsModify.Enable = false;
		this.tbMain.tbCap.btnResultsRemove.Enable = false;
		this.tbMain.tbCap.btnResultsSave.Enable = true;
		this.tbMain.tbCap.btnResultsCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnControlState(true, tbMain.tbCap, "year,WORK_ROM");
		
		this.tbMain.tbCap.btnResultsInsert.Enable = false;
		this.tbMain.tbCap.btnResultsModify.Enable = false;
		this.tbMain.tbCap.btnResultsRemove.Enable = false;
		this.tbMain.tbCap.btnResultsSave.Enable = true;
		this.tbMain.tbCap.btnResultsCancel.Enable = true;
	}
}
