//기술자 상세조회 버튼
function tbMain_tpEngeneer_Button0_OnClick(obj)
{
	var sSSN1 = tbMain.tpEngeneer.ENGINEER_SSN1.Text;
	var sSSN2 = tbMain.tpEngeneer.ENGINEER_SSN2.Text;
	var sENGINEER_NM = tbMain.tpEngeneer.ENGINEER_NM.text;
	
	if ((fnEmpty(sSSN1) || fnEmpty(sSSN2)) && fnEmpty(sENGINEER_NM))
	{
		alert('기술자 주민등록번호 또는 기술자 이름을 입력하세요.');
		return;
	}
	//2009-11-02  팝업창 _sCorp 에 COI_WRT_NUM(공사업등록번호) 값을 보낸다
	// 현재 작성중인 공사업체의 기술자가 다른 경우만 "기술자는 타업체에 재직중입니다" 처리를 하기 위해 값을 넘김
	var sCorp = dsPT_R_COMPANY_MASTER.GetColumn(0, "COI_WRT_NUM");
	var sArgs = "_sOldCode=" + quote(sSSN1 + sSSN2) + " _sNm=" + quote(sENGINEER_NM) + " _sCorp=" + quote(sCorp);
	var bRet = fnCreateDialog("st::EngListMgrD.xml", sArgs);

	
	if (bRet != null)
	{
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "ENGINEER_NM", trim(bRet[0]));
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "NM_KOR", trim(bRet[0]));
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "ENGINEER_SSN1", trim(bRet[1]));
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "ENGINEER_SSN2", trim(bRet[2]));
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "CARE_BOOK_ISSUE_NUM", bRet[3]);
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "TMP_FIELD", bRet[4]);
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "CARE_BOOK_VAL_START_DT", bRet[5]);
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "CARE_BOOK_VAL_END_DT", bRet[6]);
		//dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "TMPSSN", bRet[1] + bRet[2]);
		//dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "ENGINEER_GRADE", bRet[7]);
	}
}

function tbMain_tpEngeneer_btnEngInsert_OnClick(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpEngeneer.btnEngInsert)
	{
		sDtWorkFlag = global.gv_INSERT;

		dsPT_R_ENGINEER_CHANGE.AddRow();
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "TMP_WRT_NUM", _sKEY1);
		//dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "CHGBRE_SEQ", 
		//									dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHGBRE_SEQ"));
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "SEQ", dsPT_R_ENGINEER_CHANGE.rowcount);
		dsPT_R_ENGINEER_CHANGE.SetColumn(this.dsPT_R_ENGINEER_CHANGE.row, "WRT_ID", global.g_User_ID);
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEngeneer.btnEngModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEngeneer.btnEngRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_ENGINEER_CHANGE.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_ENGINEER_CHANGE);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=ENGINEER";	
		var sTid = "ENGINEER_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=RegChangeMgrCCUD";
		var sInDs = "dsPT_R_ENGINEER_CHANGE=dsPT_R_ENGINEER_CHANGE:u";
		var sOutDs = "dsPT_R_ENGINEER_CHANGE=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpEngeneer.btnEngCancel)
	{
		if (dsPT_R_ENGINEER_CHANGE.GetRowType(dsPT_R_ENGINEER_CHANGE.row) == "insert")
			dsPT_R_ENGINEER_CHANGE.DeleteRow(dsPT_R_ENGINEER_CHANGE.row);
		else
			fnRejectChange(dsPT_R_ENGINEER_CHANGE);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEngeneer.btnEngSave)
	{
		var sArgs = "workMODE=ENGINEER";
		var sTid = "ENGINEER_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=RegChangeMgrCCUD";
		var sInDs = "dsPT_R_ENGINEER_CHANGE=dsPT_R_ENGINEER_CHANGE:u";
		var sOutDs = "dsPT_R_ENGINEER_CHANGE=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}
