function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	
	
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		
		if (svcid == "SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
	}

	
	if (svcid == "GET_CODE")
	{
		dsPT_R_RE_DERIVERY_STATEMENT.AddRow();
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "DEFI_STE", "X");
		
		tbMain.tpBase.RECV_DT.Value = Today();
	}
	
	if (svcid == "CHECK_KEY")
	{
		if (dsCheckKey.rowcount > 0)
		{
			alert('존재하는 민원접수번호입니다.');
			tbMain.tpBase.RECV_NUM.Text = "";
			tbMain.tpBase.RECV_NUM.SetFocus();
		}
		else
		{
			global.gv_WorkFlag = global.gv_MODIFY;
			fnInitButtonBar();
			this.tbMain.tpBase.MOT_STE.Value = "STD002";
			
			var sArgs = "workMODE=MAIN";	
			var sTid = "INSERT_SAVE";
			var sUrl = "ICCS::cm/CmAction.do?cmd=RegRePublishMgrCCUD";
			var sInDs = "dsPT_R_RE_DERIVERY_STATEMENT=dsPT_R_RE_DERIVERY_STATEMENT:u";
			var sOutDs = "dsPT_R_RE_DERIVERY_STATEMENT=output";
			var sCallBack = "fn_CallBack";
			
			Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
			
		}
	}
	if (svcid == "SAVE")
	{
		alert('저장되었습니다.');
		fnInquiry();
				
		var sMOT_STE = dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "MOT_STE");
		if(sMOT_STE == "STD003"){
	    fnAllControlState(false, this);
	    divButtonBar.btnReport.enable = true;
	    divButtonBar.btnList.enable = true;
 

	    
	    if (fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "COI"))) {
	    
	    var coi_wrt_num= dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(dsPT_R_RE_DERIVERY_STATEMENT.row, "COI_WRT_NUM");
  	    var sArgs = "coi_wrt_num="+coi_wrt_num;
	    gfnCreateNewWindow("","cm::RegDetail.xml", sArgs);
	    
	    }else if(fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "WRTBOOK"))){
	    //alert("등록증");
	    var sKEY1 = dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(dsPT_R_RE_DERIVERY_STATEMENT.row, "TMP_WRT_NUM");
	    fnRegReport();
	    }else{
	    //alert("수첩+등록증");
	    fnReport();
	    
	    }
	    
	    }
	}
	if (svcid == "SEARCH")
	{
		//fnLocalControlState();
		
		var sMOT_STE = dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "MOT_STE");
		if(sMOT_STE == "STD003"){
	    fnAllControlState(false, this);
	    divButtonBar.btnReport.enable = true;
	    divButtonBar.btnList.enable = true;
	    }
		
	}	
			// 협회 송신 큐에 넣기가 성공한 경우
	if (svcid == "KICAIfSendCUD")
	{
	//alert("송신");
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "SND_STE", "O");
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "SND_DT", Today());
	}
	
	if (svcid == "INSERT_SAVE")
	{
		global.gv_WorkFlag = global.gv_MODIFY;
		fnInitButtonBar();
		
		dsPT_R_RE_DERIVERY_STATEMENT.ApplyChange();
		
		var sGroup;
		var nIndex = 0;
		
		for(var nLoop = 0; nLoop < dsPT_R_DEFICIT_CODE.rowcount; nLoop++)
		{
			sGroup = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_GROUP");
			
			if (sGroup == "GUBU")
			{
				dsPT_R_DEFICIT.AddRow();
				dsPT_R_DEFICIT.SetColumn(nIndex, "RECV_NUM", dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "RECV_NUM"));
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_SEQ", nIndex + 1);
				dsPT_R_DEFICIT.SetColumn(nIndex, "SIDO_CODE", global.g_Sd_cd);
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_CODE", dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_CODE"));
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_GROUP", dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_GROUP"));
				dsPT_R_DEFICIT.SetColumn(nIndex, "DEFI_CONT", dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "CONT"));
						
				nIndex++;
			}
		}
		
		var sArgs = "workMODE=ALL";
		var sTid = "SET_CODE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=RegRePublishMgrCCUD";
		var sInDs = "dsPT_R_DEFICIT=dsPT_R_DEFICIT:u";
		var sOutDs = "dsPT_R_DEFICIT=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	
	if (svcid == "GET_CORP")
	{
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "COI_WRT_NUM", 
							dsCOMPANY_MASTER.GetColumn(0, "COI_WRT_NUM"));
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "NAME", 
							dsCOMPANY_MASTER.GetColumn(0, "NAME"));
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "OFF_ADDR", 
							dsCOMPANY_MASTER.GetColumn(0, "ADDR_ADDR") + " " + dsCOMPANY_MASTER.GetColumn(0, "ADDR_DETAIL_ADDR"));
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "REP", 
							dsCOMPANY_MASTER.GetColumn(0, "REP_NM_KOR"));
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "COI_WRT_NUM", 
							dsCOMPANY_MASTER.GetColumn(0, "COI_WRT_NUM"));
		dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "TMP_WRT_NUM", 
							dsCOMPANY_MASTER.GetColumn(0, "TMP_WRT_NUM"));
	}
	
	if (svcid == "COMPANY_SAVE")
	{
		alert('저장되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	if (svcid == "COMPANY_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	
	if (svcid == "MEMO_SAVE")
	{
		alert('저장되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	if (svcid == "MEMO_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	
	if (svcid == "SET_CODE")
	{
		dsPT_R_DEFICIT.ApplyChange();
	}
}

