	fnTablesBinding();
	
	global.gv_WorkFlag = global.gv_MODIFY;
	fnInitButtonBar();
}

/**
 1. 함수명 : fnSave
 2. 기  능 : 저장버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnSave()
{
	//var bRet = fnValidation();
	//if (bRet == false) return;
			if ( this.tbMain.tpBase.MOT_STE.Value == "STD003" )
	{
				
		if (fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "COI_WRT_NUM")))
		{
			alert('등록번호가 입력되지 않았습니다.');
			this.tbMain.tpBase.MOT_STE.Value = "STD002";
			return;
		}
		
			if (fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "COI")) && fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "WRTBOOK")))
		{
			var sMsg = "등록증 또는 등록수첩이 입력되지 않았습니다.\n\n";
					sMsg += "   등록을 완료할 수 없습니다. ";
			alert(sMsg);
			this.tbMain.tpBase.MOT_STE.Value = "STD002";
			
			return;		
		}
		/*
			if (fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "WRTBOOK")))
		{
			var sMsg = "등록수첩이 입력되지 않았습니다.\n\n";
					sMsg += "   등록을 완료할 수 없습니다. ";
			alert(sMsg);
			this.tbMain.tpBase.MOT_STE.Value = "STD002";
			
			return;		
		}
		*/
		if (fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "APPL_CAUSE")))
		{
			var sMsg = "신청사유가 입력되지 않았습니다.\n\n";
					sMsg += "   등록을 완료할 수 없습니다. ";
			alert(sMsg);
			this.tbMain.tpBase.MOT_STE.Value = "STD002";
			
			return;		
		}
	}
	if (dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "DEFI_STE") == "O")
	{
			dsPT_R_DELIVERY_BOOK.AddRow();
			dsPT_R_DELIVERY_BOOK.SetColumn(dsPT_R_DELIVERY_BOOK.row, "TMP_WRT_NUM", 
						dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "TMP_WRT_NUM"));
			dsPT_R_DELIVERY_BOOK.SetColumn(dsPT_R_DELIVERY_BOOK.row, "DELI_CAUSE", 
						dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "APPL_CAUSE"));
			dsPT_R_DELIVERY_BOOK.SetColumn(dsPT_R_DELIVERY_BOOK.row, "DELI_YMD", Today());
			
			dsPT_R_DELIVERY_BOOK.SetColumn(dsPT_R_DELIVERY_BOOK.row, "WRT_ID", global.g_User_ID);
		if (dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "COI") == "1")
		{
			dsPT_R_DELIVERY_BOOK.SetColumn(dsPT_R_DELIVERY_BOOK.row, "DELI_CLASS", "COI");
		} 
		else if (dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "WRTBOOK") == "1")
		{
			dsPT_R_DELIVERY_BOOK.SetColumn(dsPT_R_DELIVERY_BOOK.row, "DELI_CLASS", "BOOK");
		}
	}

	var sArgs = "workMODE=ALL";
	var sTid = "SAVE";
	var sUrl = "ICCS::cm/CmAction.do?cmd=RegRePublishMgrCCUD";
	var sInDs = "dsPT_R_RE_DERIVERY_STATEMENT=dsPT_R_RE_DERIVERY_STATEMENT:u dsPT_R_DEFICIT=dsPT_R_DEFICIT:u dsPT_R_DELIVERY_BOOK=dsPT_R_DELIVERY_BOOK:u";
	var sOutDs = "dsPT_R_RE_DERIVERY_STATEMENT=output dsPT_R_DEFICIT=output2 dsPT_R_DELIVERY_BOOK=output3";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	
			if ( this.tbMain.tpBase.MOT_STE.Value == "STD003" )
	{
		// 등록완료시 협회송신 큐에 넣기
		dsPT_TMP_TRANS_KEY.DeleteAll();
		dsPT_TMP_TRANS_KEY.AddRow();
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "WORK_CLASS_CODE", "R00013");
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY1", 
					dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "TMP_WRT_NUM"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY2", 
		            dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "RECV_NUM"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY3", 
		            dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "SIDO_CODE"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY4", 
		            dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "COI_WRT_NUM"));
		if(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "SND_STE") == "O")
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "U");
		else
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "C");
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "SIDO_CODE", global.g_Sd_cd);
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "INS_DT", Today());
		
		http.Sync = true;
		var sArgs = "";		
		var sTid = "KICAIfSendCUD";
		var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=KICAIfSendCUD";
		var sInDs = "dsPT_TMP_TRANS_KEY=dsPT_TMP_TRANS_KEY:u";
		var sOutDs = "dsPT_TMP_TRANS_KEY=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		http.Sync = false;
}
	if ( this.tbMain.tpBase.MOT_STE.Value == "STD003" )
	{
	divButtonBar.btnReport.enable = true;
	}

}

/**
 1. 함수명 : fnReport
 2. 기  능 : 출력버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnReport()
{
	var sCode = fnCreateDialog("cm::RegRePublishReportDlgN.xml", "");
	var sKEY1 = dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(dsPT_R_RE_DERIVERY_STATEMENT.row, "TMP_WRT_NUM");
	
	var coi_wrt_num= dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(dsPT_R_RE_DERIVERY_STATEMENT.row, "COI_WRT_NUM");
	//alert("1"+sKEY1);
	//alert("2"+coi_wrt_num);
	//등록 수첩 리포트1
	if (sCode == "RPT1")
	{
	var coi_wrt_num= dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(dsPT_R_RE_DERIVERY_STATEMENT.row, "COI_WRT_NUM");

	var sArgs = "coi_wrt_num="+coi_wrt_num;

	gfnCreateNewWindow("","cm::RegDetail.xml", sArgs);
	}
	// 등록증 리포트
	else if (sCode == "RPT2")
	{
		fnRegReport();
	}
	// 등록대장 리포트
	else if (sCode == "RPT3")
	{
	
	
		var sViewer = "_sViewer=NewRegStR";
		var sReportname =  " _sReportname=connection.reportname=/BusinessRegist/NewRegStR.ozr";
		var sOdiNames = " _sOdiNames=odi.odinames=Q)NewRegStR";
		var	spCount = " _spCount=odi.Q)NewRegStR.pcount=1";
		var sParam = " _sParam=odi.Q)NewRegStR.args1=TMP_WRT_NUM=" + sKEY1;
		
		var sRet = fnCreateDialogResize("bas::frmReport.xml", sViewer + sReportname + sOdiNames + spCount + sParam);
	}
	
}

function fnReport2()
{
	var sCode = fnCreateDialog("cm::RegRePublishReportDlg.xml", "");
	var sKEY1 = dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(dsPT_R_RE_DERIVERY_STATEMENT.row, "TMP_WRT_NUM");

	// 등록 수첩 리포트
	if (sCode == "RPT1")
	{
		fnBookModify();
	}
	// 등록증 리포트
	else if (sCode == "RPT2")
	{
		fnRegReport();
	}
}

