function fnValidation()
{
	var nIndex = 0;
	dsError.DeleteAll();
	
	if (fnEmpty(dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "TMP_WRT_NUM")))
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "btnCMStD");
		dsError.SetColumn(nIndex, "TEXT", "공사업체를 선택하세요.");
		nIndex++;
	}
	
	if (dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "COI") != "1"
		&& dsPT_R_RE_DERIVERY_STATEMENT.GetColumn(0, "WRTBOOK") != "1")
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "chkCOI");
		dsError.SetColumn(nIndex, "TEXT", "재발급 항목을 선택하세요.");
		nIndex++;
	}
	
	for(var nLoop = 0; nLoop < dsPT_R_DEFICIT.rowcount; nLoop++)
	{
		if(dsPT_R_DEFICIT.GetColumn(nLoop, "DEFI_OUT") != "1")
		{
			//nErrCnt++;
			dsError.AddRow();
			dsError.SetColumn(nIndex, "FLD_NAME", "grdPaper");
			dsError.SetColumn(nIndex, "TEXT", "흠결이 완료되지 않았습니다.");
			nIndex++;
			break;
		}
	}
	
	if (nIndex == 0) dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "DEFI_STE", "O");
	else dsPT_R_RE_DERIVERY_STATEMENT.SetColumn(0, "DEFI_STE", "X");
	
	if (nIndex > 0)
	{
		var nRet = fnCreateDialog("./frmErrorDLG.xml", "");
		if (nRet < 0) return true;
		else 
		{
			fnFindComponent(this, dsError.GetColumn(nRet, "FLD_NAME"));
			return false;
		}
	}
	
	return true;
}


