//////////////////////////////////////////////////////////////////
// Memo Detail Event
//////////////////////////////////////////////////////////////////
function fnDetailButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpPaper.btnMemoInsert)
	{
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_MEMO.AddRow();
		dsPT_R_MEMO.SetColumn(dsPT_R_MEMO.row, "RECV_NUM", tbMain.tpBase.RECV_NUM.Text);
		dsPT_R_MEMO.SetColumn(dsPT_R_MEMO.row, "MEMO_SEQ", dsPT_R_MEMO.rowcount);
		dsPT_R_MEMO.SetColumn(dsPT_R_MEMO.row, "SIDO_CODE", global.g_Sd_cd);
		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpPaper.btnMemoModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpPaper.btnMemoRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_MEMO.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_MEMO);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		
		var sArgs = "workMODE=MEMO";
		var sTid = "MEMO_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=RegRePublishMgrCCUD";
		var sInDs = "dsPT_R_MEMO=dsPT_R_MEMO:u";
		var sOutDs = "dsPT_R_MEMO=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpPaper.btnMemoCancel)
	{
		fnRejectChange(dsPT_R_MEMO);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpPaper.btnMemoSave)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		var sArgs = "workMODE=MEMO";	
		var sTid = "MEMO_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=RegRePublishMgrCCUD";
		var sInDs = "dsPT_R_MEMO=dsPT_R_MEMO:u";
		var sOutDs = "dsPT_R_MEMO=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitMemoGroup(sDtWorkFlag)
{

	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnControlState(false, tbMain.tpPaper, "INS_DT,MEMO_CONT");
		fnControlState(true, tbMain.tpPaper, "grdMemo");
		
		tbMain.tpPaper.btnMemoInsert.Enable = true;
		tbMain.tpPaper.btnMemoModify.Enable = true;
		tbMain.tpPaper.btnMemoRemove.Enable = true;
		tbMain.tpPaper.btnMemoSave.Enable = false;
		tbMain.tpPaper.btnMemoCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnControlState(true, tbMain.tpPaper,  "INS_DT,MEMO_CONT");
		fnControlState(false, tbMain.tpPaper, "grdMemo");
		
		tbMain.tpPaper.btnMemoInsert.Enable = false;
		tbMain.tpPaper.btnMemoModify.Enable = false;
		tbMain.tpPaper.btnMemoRemove.Enable = false;
		tbMain.tpPaper.btnMemoSave.Enable = true;
		tbMain.tpPaper.btnMemoCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnControlState(true, tbMain.tpPaper, "INS_DT,MEMO_CONT");
		fnControlState(false, tbMain.tpPaper, "grdMemo");
		
		tbMain.tpPaper.btnMemoInsert.Enable = false;
		tbMain.tpPaper.btnMemoModify.Enable = false;
		tbMain.tpPaper.btnMemoRemove.Enable = false;
		tbMain.tpPaper.btnMemoSave.Enable = true;
		tbMain.tpPaper.btnMemoCancel.Enable = true;
	}
}

