function fnValidation()
{
	var nIndex = 0;
	dsError.DeleteAll();
	
	if(DateTime(tbMain.tpBase.RECV_DT.Value) > DateTime(tbMain.tpBase.PROC_LIM.Value))
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "PROC_LIM");
		dsError.SetColumn(nIndex, "TEXT", "민원처리일자가 민원접수일자보다 작습니다.");
		
		nIndex++;
	}
	
	// wbjeon 2010-09-30
	//if((DateTime(tbMain.tpBase.PROC_LIM.Value) - DateTime(tbMain.tpBase.RECV_DT.Value)) > 14)
	if (fromDayAfter(tbMain.tpBase.PROC_LIM.Value,-14,0,"add") != replace(ToString(DateTime(tbMain.tpBase.RECV_DT.Value)),"-",""))
	{
		//dsError.AddRow();
		//dsError.SetColumn(nIndex, "FLD_NAME", "PROC_LIM");
		//dsError.SetColumn(nIndex, "TEXT", "민원처리일자가 14일을 초과합니다.");
		
		alert("민원처리일자가 14일을 초과합니다.");
		
		//nIndex++;
	}
	
	if(fnEmpty(tbMain.tpBase.NAME.Text))
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "NAME");
		dsError.SetColumn(nIndex, "TEXT", "회사 상호가 입력되지 않았습니다.");
		
		nIndex++;
	}
	
	if(fnEmpty(tbMain.tpBase.REP_NM_KOR.Text))
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "REP_NM_KOR");
		dsError.SetColumn(nIndex, "TEXT", "대표자 성명이 입력되지 않았습니다.");
		
		nIndex++;
	}
	
	/*if(fnEmpty(tbMain.tpBase.MANA_NUM.Value))
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "MANA_NUM");
		dsError.SetColumn(nIndex, "TEXT", "사업자 등록번호가 입력되지 않았습니다.");
		
		nIndex++;
	}
	
	if(_sCheck == "X")
	{
		dsError.AddRow();
		dsError.SetColumn(nIndex, "FLD_NAME", "MANA_NUM");
		dsError.SetColumn(nIndex, "TEXT", "사업자 번호 중복체크를 실시하세요.");
		
		nIndex++;
	}*/

	//var nErrCnt = 0;
	for(var nLoop = 0; nLoop < dsPT_R_DEFICIT.rowcount; nLoop++)
	{
		if(dsPT_R_DEFICIT.GetColumn(nLoop, "DEFI_OUT") != "1")
		{
			//nErrCnt++;
			dsError.AddRow();
			dsError.SetColumn(nIndex, "FLD_NAME", "grdPaper");
			dsError.SetColumn(nIndex, "TEXT", "흠결이 완료되지 않았습니다.");
			nIndex++;
			break;
		}
	}
	
	if (nIndex == 0) dsPT_R_REGIST_STATEMENT.SetColumn(0, "DEFI_STE", "O");
	else dsPT_R_REGIST_STATEMENT.SetColumn(0, "DEFI_STE", "X");
	
	if (nIndex > 0)
	{
		var nRet = fnCreateDialog("cm::frmErrorDLG.xml", "");
		if (nRet < 0) return true;
		else 
		{
			fnFindComponent(this, dsError.GetColumn(nRet, "FLD_NAME"));
			return false;
		}
	}
	
	return true;
}


