//////////////////////////////////////////////////////////////////
// 코드 조회용 버튼 이벤트
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnEditButton_Click
 2. 기  능 : 에디트 버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnEditButton_Click(obj)
{
	//겸업사항 코드 조회
	if (obj == this.tbMain.tpBase.btnComDivCode)
	{		
		var sArgs = "_sOldCode=SUBSIDIARY";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;

		dsPT_R_SUBSIDIARY_COMPANY.SetColumn(dsPT_R_SUBSIDIARY_COMPANY.row, "OTOB_COM_CODE", sReturn[0]);
		dsPT_R_SUBSIDIARY_COMPANY.SetColumn(dsPT_R_SUBSIDIARY_COMPANY.row, "OTOB_COM_TOB_CONT", sReturn[1]);

		return;
	}
	
	//우편번호코드 조회
	if (obj == this.tbMain.tpBase.btnPostCode)
	{		
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		if (sReturn == null) return;
		
		this.tbMain.tpBase.ADDR_POST_NUM = sReturn[0].replace("-","");
		this.tbMain.tpBase.ADDR_ADDR = sReturn[1];
	}
}

//////////////////////////////////////////////////////////////////
// Key Down 관련 이벤트
//////////////////////////////////////////////////////////////////
function fnKey_Down(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	if(nChar == "49" && bControl == true) fnInsert();
	else if(nChar == "50" && bControl == true) fnModify();
	else if(nChar == "51" && bControl == true) fnRemove();
	else if(nChar == "83" && bControl == true) fnSave();
	else if(nChar == "88" && bControl == true) Close();
	else if(nChar == "80" && bControl == true) fnReport();
	else if(nChar == "13") 
	{
		//TextArea를 제외한 다른컴포넌트들은 이동
		var sCompnentname = GetCurrentComponent(true);
		if(sCompnentname.GetType() == "TextArea") return;
		GetNextComponent(true).SetFocus();
	}
	//F9 키를누르면 탭간 이동
	else if(nChar == "120") 
	{
		var nTabIndex = tbMain.TabIndex;
		
		if(nTabIndex == tbMain.TabCount - 1)
			tbMain.TabIndex = 0;
		else
			tbMain.TabIndex = nTabIndex + 1;
	}
}

//////////////////////////////////////////////////////////////////
// Validation check 이벤트
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnKillFocus
 2. 기  능 : Control Leave 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnKillFocus(obj)
{
	//접수일자 선택 시 처리기한을 14일 이후로 설정
	if (obj == tbMain.tpBase.RECV_DT)
	{
	//	tbMain.tpBase.PROC_LIM.Value = AddDate(tbMain.tpBase.RECV_DT.Value, 13);
	//	wbjeon 2010-09-30 수정 		 
		tbMain.tpBase.PROC_LIM.Value = fromDayAfter(tbMain.tpBase.RECV_DT.Value,14,0,"add");
	}
	
	if (obj == tbMain.tpBase.REP_NM_KOR)
	{
		if( fnEmpty(tbMain.tpBase.REP_NM_HAN.Text) )
			tbMain.tpBase.REP_NM_HAN.Text = tbMain.tpBase.REP_NM_KOR.Text;
	}
}

