	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnControlState(true, tbMain.tpBase, "OTOB_COM_NUM,btnComDivCode");
		fnControlState(false, tbMain.tpBase, "grdCOMPANY,OTOB_COM_TOB_CONT");
		
		tbMain.tpBase.btnCInsert.Enable = false;
		tbMain.tpBase.btnCModify.Enable = false;
		tbMain.tpBase.btnCRemove.Enable = false;
		tbMain.tpBase.btnCSave.Enable = true;
		tbMain.tpBase.btnCCancel.Enable = true;
	}
}

