#include "UserLib::CommonJs.js"
//////////////////////////////////////////////////////////////////
// 전역변수
//////////////////////////////////////////////////////////////////
var	_sKEY1;
var	_sKEY2;
var _sWorkFlag;
var _RecvDt;
 
//////////////////////////////////////////////////////////////////
// Form Load 이벤트
//////////////////////////////////////////////////////////////////
function fnForm_Loaded(obj)
{
	fnSetYear();
	
	global.gv_WorkFlag = _sWorkFlag;
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnInquiry();
	}
	else if(global.gv_WorkFlag == global.gv_INSERT)
	{
		global.gv_WorkFlag = global.gv_INSERT;
		fnInitButtonBar();
	}
	else if(global.gv_WorkFlag == global.gv_MODIFY)
	{
		fnModify();
	}
	
	fnInitReperGroup("INQUIRY");
	fnInitEngineerGroup("INQUIRY");
	fnInitMemoGroup("INQUIRY");
	fnInitCompanyGroup("INQUIRY");
	fnInitRefusalGroup("INQUIRY");
	
	tbMain.tpAmt.DIAGNOSTIC_DIV.Value = "DIG003";
	tbMain.tpAmt.CAPITAL_CHECK_YN = "P1";
	
	
	divButtonBar.Image1.text = space(6) + "기준신고 입력";
	
	tbMain.MoveTab(1, 0);
	tbMain.MoveTab(5, 1);
	tbMain.TabIndex = 0;
	
}

function fnForm_Activate(obj)
{
	global.g_activeFrm = this.Id;
}

//////////////////////////////////////////////////////////////////
// 공통기능
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnInitButtonBar
 2. 기  능 : 화면상단 버튼상태 설정
 3. Params 
    없음
 4. Return
    없음
*/
function fnInitButtonBar()
{
	if (global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, this);

		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = true;
		divButtonBar.btnRemove.enable = false;

		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, false);
		divButtonBar.btnCancel.enable = false;
		divButtonBar.btnReport.enable = true;
		divButtonBar.btnExcel.enable = true;

		btnGetREC.Enable = false;
		fnLocalControlState();
	}
	else if (global.gv_WorkFlag == global.gv_INSERT)
	{
		fnAllControlState(true, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnCancel.enable = true;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
				
		btnGetREC.Enable = true;
		fnLocalControlState();
	}
	else
	{
		fnAllControlState(true, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnCancel.enable = true;
		divButtonBar.btnReport.enable = true;
		divButtonBar.btnExcel.enable = false;
				
		fnControlState(false, this, "RECV_NUM,NAME,COI_WRT_NUM");
		btnGetREC.Enable = false;
		fnLocalControlState();
	}
}

/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	var sArgs = "workMODE=StdMgrC scTMP_WRT_NUM=" + quote(_sKEY1) + " scRECV_NUM=" + quote(_sKEY2);
	var sTid = "SEARCH";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrC";
	var sInDs = "";
	var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_CHANGE_HISTORY=output2 " ;
		sOutDs += "dsPT_R_REFFER=output8 dsPT_R_ENGINEER_CHANGE=output7 " ;
		sOutDs += "dsPT_R_REG_MEMO=output9 dsPT_R_REFUSAL_REASON=output10 ";
		sOutDs += "dsPT_R_SUBSIDIARY=output11 dsPT_R_COM_DEFICIT=output12 dsPT_R_MOT_STE=output13 ";
		
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	http.sync = false;
	
	//alert( svcid );
	
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
	}
	
	if(svcid == "SEARCH")
	{
		for (var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
		{
			dsPT_R_REFFER.SetColumn(nLoop, "SEND_YN", "");
			dsPT_R_REFFER.SetColumn(nLoop, "CHAR_REF_YN", "");
		}
	
		fnLocalControlState();
	}
	
	if (svcid == "GET_RECV_NUM")
	{
		if (dsPT_R_REGIST_STATEMENT.GetColumn(0, "DEFI_STE") != "O")
		{
			alert('선택하신 접수건은 흠결이 완료되지 않았습니다.');
			Close();
		}
		
		fnInsert();
	}
	
	if (svcid == "REFER_CHECK")
	{
		if (dsReferCheck.rowcount > 0)
		{
			if (fnEmpty(dsReferCheck.GetColumn(0, "RETIRE_DATE")))
			{
				alert('입력하신 조회 대상자는 타 공사업체에 재직중입니다.');
				return;
			}
			else
			{
				alert('조회 대상자는 등록이 가능한 사람입니다.');
			}
		}
	}
	
	if (svcid == "GET_CORP")
	{
		_sKEY1 = dsPT_R_COMPANY_MASTER.GetColumn(0, "TMP_WRT_NUM");
		var sMax_Seq = dsMAX_SEQ.GetColumn(0, "CHGBRE_SEQ");
		
		fnSetMasterDetail("dsPT_R_COMPANY_MASTER", "dsPT_R_BASIC_CHANGE_HISTORY");
		
		//dsPT_R_BASIC_CHANGE_HISTORY.AddRow();
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "RECV_NUM", _sKEY2);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "CHGBRE_SEQ", sMax_Seq);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "MOT_STE", "STD002");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "STA_CHG_CLASS_CODE", "R00002");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "SND_STE", "X");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "WRT_ID", global.g_User_ID);
		
		// 2010.01.28. 접수일자 넣기
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "REC_DT", _recvDt);

		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "COMPANY_DIAG_CLASS_CODE", "P2");
		
		tbMain.tpAmt.DIAGNOSTIC_DIV.Value = "DIG003";
		tbMain.tpAmt.CAPITAL_CHECK_YN = "P1";

		//신원조회 대상자의 초기값 설정
		for (var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
		{
			dsPT_R_REFFER.SetColumn(nLoop, "SEND_YN", "");
			dsPT_R_REFFER.SetColumn(nLoop, "CHAR_REF_YN", "");
		}

		var sGroup;
		var sCode;
		var sContent;
		var sSeq;
		
		var nIndex = 0;
		//흠결
		for(var nLoop = 0; nLoop < dsPT_R_DEFICIT_CODE.rowcount; nLoop++)
		{
			sGroup = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_GROUP");
			if(sGroup == "DRBC")
			{
				sCode = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_CODE");
				sContent = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "CONT");
				sSeq = nIndex + 1;

				dsPT_R_COM_DEFICIT.AddRow();
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "TMP_WRT_NUM", _sKEY1);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "SEQ", sSeq);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "CHGBRE_SEQ", sMax_Seq);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "DEFI_CODE", sCode);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "DEFI_CONT", sContent);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "DEFI_GROUP", sGroup);
				
				nIndex++;
			}
		}
		
		// 접수시 메모 가져오기
		for(var nLoop = 0; nLoop < dsPT_R_MEMO.rowcount; nLoop++)
		{
			dsPT_R_REG_MEMO.AddRow();
			dsPT_R_REG_MEMO.SetColumn(nLoop, "TMP_WRT_NUM", _sKEY1);
			dsPT_R_REG_MEMO.SetColumn(nLoop, "CHGBRE_SEQ", sMax_Seq);
			dsPT_R_REG_MEMO.SetColumn(nLoop, "MEMO_SEQ", dsPT_R_MEMO.GetColumn(nLoop, "MEMO_SEQ"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "MEMO_CONT", dsPT_R_MEMO.GetColumn(nLoop, "MEMO_CONT"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "WRT_DT", dsPT_R_MEMO.GetColumn(nLoop, "WRT_DT"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "UPD_DT", dsPT_R_MEMO.GetColumn(nLoop, "UPD_DT"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "INS_DT", dsPT_R_MEMO.GetColumn(nLoop, "INS_DT"));
			
		}
		
		var sArgs = "workMODE=ALL";
		var sTid = "INSERT_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUD";
		var sInDs = "dsPT_R_COMPANY_MASTER=dsPT_R_COMPANY_MASTER:u dsPT_R_BASIC_CHANGE_HISTORY=dsPT_R_BASIC_CHANGE_HISTORY:u dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u ";
		var sOutDs = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_CHANGE_HISTORY=output2 dsPT_R_SUBSIDIARY=output4 ";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	
	if (svcid == "INSERT_SAVE")
	{
		fnSetMasterDetail("dsPT_R_BASIC_CHANGE_HISTORY", "dsPT_R_MOT_STE");
	}

	if (svcid == "SAVE")
	{
		alert('저장되었습니다.');
		fnInquiry();
	}

	if (svcid == "REPER_REPORT")
	{
		fnReferReport();
	}
	if (svcid == "REPER_SAVE")
	{
		alert('저장되었습니다.');
		fnInitReperGroup("INQUIRY");
	}
	if (svcid == "REPER_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitReperGroup("INQUIRY");
	}
	if (svcid == "ENGINEER_SAVE")
	{
		alert('저장되었습니다.');
		fnInitEngineerGroup("INQUIRY");
	}
	if (svcid == "ENGINEER_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitEngineerGroup("INQUIRY");
	}
	
	if (svcid == "MEMO_SAVE")
	{
		alert('저장되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	if (svcid == "MEMO_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	if (svcid == "REFUSAL_SAVE")
	{
		alert('저장되었습니다.');
		fnInitRefusalGroup("INQUIRY");
	}
	if (svcid == "REFUSAL_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitRefusalGroup("INQUIRY");
	}
	if (svcid == "COMPANY_SAVE")
	{
		alert('저장되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	if (svcid == "COMPANY_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	
}

//////////////////////////////////////////////////////////////////
// 툴바 버튼 이벤트 핸들러
//////////////////////////////////////////////////////////////////
function fnInquiry()
{
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
}

/**
 1. 함수명 : fnInsert
 2. 기  능 : 신규버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnInsert()
{

}

function fnCancel()
{
	if(!Confirm('취소하시겠습니까?')) return;
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		//키값을 자동으로 생성할 경우 삭제하는 로직을 추가해주세요
		Close();
	}
	else
	{
		fnRejectChange(this.dsPT_R_COMPANY_MASTER);
		fnRejectChange(this.dsPT_R_BASIC_CHANGE_HISTORY);
		alert('취소되었습니다.');
	}

	fnInquiry();
}

/**
 1. 함수명 : fnModify
 2. 기  능 : 수정버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnModify()
{	
	fnTablesBinding();
	
	global.gv_WorkFlag = global.gv_MODIFY;
	fnInitButtonBar();
}

/**
 1. 함수명 : fnRemove
