 3. Params 
    없음
 4. Return
    없음
*/
function fnRemove()
{
}

/**
 1. 함수명 : fnSave
 2. 기  능 : 저장버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnSave()
{
	if (MOT_STE.Value == "STD003")
	{
	
		//var wrtDt = Today();
		var wrtDt = TMP_WRT_DT.Value;
		
		if( fnEmpty(wrtDt) ) {
			alert( "등록완료일자를 입력해주십시오." );
			return;
		}
		
		if (fnEmpty(dsPT_R_COMPANY_MASTER.GetColumn(0, "COI_WRT_NUM")))
		{
			alert('공사업등록번호가 입력되지 않았습니다.');
			MOT_STE.Value = "2";
			TMP_WRT_DT.Enable = false;
			return;
		}
		
		// 2010.01.08. 개인사업자의 경우 납입자본금을 입력할 수 없다.
		/*
		if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "PAY_CAP")))
		{
			alert('납입 자본금 정보가 입력되지 않았습니다.');
			MOT_STE.Value = "2";
			TMP_WRT_DT.Enable = false;
			return;
		}
		*/
		
		if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "TUP_AOM")))
		{
			alert('자본금 예치 정보가 입력되지 않았습니다.');
			MOT_STE.Value = "2";
			TMP_WRT_DT.Enable = false;
			return;
		}
		
		if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "REA_CAP")))
		{
			alert('실질 자본금 정보가 입력되지 않았습니다.');
			MOT_STE.Value = "2";
			TMP_WRT_DT.Enable = false;
			return;
		}
		
		var nAmt = toNumber(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "PAY_CAP"));
		var nRAmt = toNumber(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "REA_CAP"));
		var nTAmt = toNumber(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "TUP_AOM"));
/*		
		if (dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "COMMANA_CLASS") == "1" && nAmt < 150000)
		{
			var sMsg = "법인의 경우 납입 자본금이 150,000,000 이상 등록이 가능합니다\n\n";
				sMsg += "                등록을 완료할 수 없습니다.                ";
			alert(sMsg);
			MOT_STE.Value = "2";
			return;
		}
*/		
		
		if (dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "COMMANA_CLASS") == "1" && nRAmt < 150000)
		{
			var sMsg = "법인의 경우 실질 자본금이 150,000,000 이상 등록이 가능합니다\n\n";
				sMsg += "                등록을 완료할 수 없습니다.                ";
			alert(sMsg);
			MOT_STE.Value = "2";
			TMP_WRT_DT.Enable = false;
			return;
		}
		
		if (dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "COMMANA_CLASS") == "2" && nRAmt < 200000)
		{
			var sMsg = "개인의 경우 실질 자본금이 200,000,000 이상 등록이 가능합니다\n\n";
				sMsg += "                등록을 완료할 수 없습니다.                ";
			alert(sMsg);
			MOT_STE.Value = "2";
			TMP_WRT_DT.Enable = false;
			return;
		}
		
		var nEngCnt = 0;
		for(var nLoop = 0; nLoop < dsPT_R_ENGINEER_CHANGE.rowcount; nLoop++)
		{
			if (fnEmpty(dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "RET_YMD")))
				nEngCnt++;
		}
		
		//if (nEngCnt < 4)  3인이상 2009-09-23 수정
		if (nEngCnt <= 2) 
		{
			var sMsg = "정보통신 기술자 보유숫자가 부족합니다.\n\n";
				sMsg += "    등록을 완료할 수 없습니다.     ";
			alert(sMsg);
			MOT_STE.Value = "STD002";
			TMP_WRT_DT.Enable = false;
			return;
		}
		else
		{
			var sEngVal = "X";
			for (var nLoop = 0; nLoop < dsPT_R_ENGINEER_CHANGE.rowcount; nLoop++)
			{
				var sGrade = dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "TMP_FIELD");
				//var sGrade = dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "TMP_GRADE"); //2009-09-23 수정 java 필드정보와 불일치함
				if ((fnEmpty(dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "RET_YMD")) && sGrade == "특급") 
					|| (fnEmpty(dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "RET_YMD")) && sGrade == "중급") 
					|| (fnEmpty(dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "RET_YMD")) && sGrade == "고급"))
				{
					sEngVal = "O";
					break;
				}
			}
			
			if(sEngVal == "X")
			{
				var sMsg = "중급이상 기술자 보유숫자가 부족합니다.\n\n";
					sMsg += "    등록을 완료할 수 없습니다.     ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				TMP_WRT_DT.Enable = false;
				return;
			}
		}
		
		//  2009-10-28 20 -> 15제곱미터임 		
		//if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "OFFICE_AREA")) 
		//	|| ToInteger(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "OFFICE_AREA")) < 20)
		if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "OFFICE_AREA")) 
			|| ToInteger(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "OFFICE_AREA")) <= 14)	
						
		{
			var sMsg = "사무실 면적이 부족합니다.\n\n";
					sMsg += "등록을 완료할 수 없습니다. ";
			alert(sMsg);
			MOT_STE.Value = "STD002";
			TMP_WRT_DT.Enable = false;
			return;		
		}
		
		if (dsPT_R_REFUSAL_REASON.rowcount > 0)
		{
			var sMsg = "등록 결격 사유가 존재합니다.\n\n";
					sMsg += "   등록을 완료할 수 없습니다. ";
			alert(sMsg);
			MOT_STE.Value = "STD002";
			TMP_WRT_DT.Enable = false;
			return;
		}
		
		if (tbMain.tpAmt.DIAGNOSTIC_DIV.Value = "DIM001")
		{
			if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "COMPANY_DIAG_ISSUE_DT")))
			{
				var sMsg = "기업진단보고서 발급일자가 입력되지 않았습니다..\n\n";
						sMsg += "   등록을 완료할 수 없습니다. ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				TMP_WRT_DT.Enable = false;
				return;		
			}
		}
		
		if (tbMain.tpAmt.DIAGNOSTIC_DIV.Value = "DIM001")
		{
			if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "COMPANY_DIAG_BAS_DT")))
			{
				var sMsg = "기업진단보고서 기준일자가 입력되지 않았습니다..\n\n";
						sMsg += "   등록을 완료할 수 없습니다. ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				TMP_WRT_DT.Enable = false;
				return;		
			}
		
			var sDay = dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "COMPANY_DIAG_BAS_DT"); 
			
			// 2009-09-18 수정 방송통신위원회고시 제2009-16호 30일->45일변경
			//if(Today() > AddDate(sDay, 37)) 
			//if(Today() > AddDate(sDay, 45))
			if(sDay > AddDate(sDay, 45))
			{
				var sMsg = "기업진단보고서가 45일 이내에 발급되지 않았습니다.\n\n";
					sMsg += "                등록을 완료할 수 없습니다.                 ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				TMP_WRT_DT.Enable = false;
				return;		
			}
		}
		else
		{
			if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "COMPANY_DIAG_BAS_DT2")))
			{
				var sMsg = "재무제표 발급년도가 입력되지 않았습니다..\n\n";
						sMsg += "   등록을 완료할 수 없습니다. ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				TMP_WRT_DT.Enable = false;
				return;	
			}
		}
		
		for(var nLoop = 0; nLoop < dsPT_R_ENGINEER_CHANGE.rowcount; nLoop++)
		{
			if (fnEmpty(dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "EMPL_YMD")))
				dsPT_R_ENGINEER_CHANGE.SetColumn(nLoop, "EMPL_YMD", wrtDt);
		}
		
		// 2010.01.11. 기준신고완료일자를 무조건 갱신한다.
		//if (fnEmpty(dsPT_R_COMPANY_MASTER.GetColumn(0, "BAS_STA_DT")))
		//{
			dsPT_R_COMPANY_MASTER.SetColumn(0, "BAS_STA_DT", wrtDt);
			dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "BAS_STA_DT", wrtDt);
		//}
		
		//if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHG_DT")))
			dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "CHG_DT", wrtDt);
		
		// 2010.01.29. 세부변경 사항 선택
		var sArgs = " _parentType=Std _chgCode=" + dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "EXT_CHG_CLASS_CODE");
		var sReturn = fnCreateDialog("cm::frmChgSelectDlg.xml", sArgs);
		
		if( sReturn == null )
			return;
		
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "EXT_CHG_CLASS_CODE", sReturn);
			
	}
	else {
		// 등록완료가 아닐 경우 무조건 완료일자를 없앤다.
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "CHG_DT", "");
	}
	
	//fnSetDetailDataset();
	
	if (MOT_STE.Value == "STD003")
	{
		// 등록완료시 협회송신 큐에 넣기
		dsPT_TMP_TRANS_KEY.DeleteAll();
		dsPT_TMP_TRANS_KEY.AddRow();
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "WORK_CLASS_CODE", 
					dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "STA_CHG_CLASS_CODE"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY1", 
					dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "TMP_WRT_NUM"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY2", 
					dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHGBRE_SEQ"));
		if(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "SND_STE") == "O")
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "U");
		else
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "C");
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "SIDO_CODE", global.g_Sd_cd);
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "INS_DT", Today());
		
		http.Sync = true;
		var sArgs = "";		
		var sTid = "KICAIfSendCUD";
		var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=KICAIfSendCUD";
		var sInDs = "dsPT_TMP_TRANS_KEY=dsPT_TMP_TRANS_KEY:u";
		var sOutDs = "dsPT_TMP_TRANS_KEY=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		http.Sync = false;
	}

	var sArgs = "workMODE=ALL";
	var sTid = "SAVE";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUD";
	//var sInDs = "dsPT_R_COMPANY_MASTER=dsPT_R_COMPANY_MASTER:u dsPT_R_BASIC_CHANGE_HISTORY=dsPT_R_BASIC_CHANGE_HISTORY:u dsPT_R_MOT_STE=dsPT_R_MOT_STE:u";
	var sInDs = "dsPT_R_BASIC_CHANGE_HISTORY=dsPT_R_BASIC_CHANGE_HISTORY:u dsPT_R_MOT_STE=dsPT_R_MOT_STE:u";
	//var sOutDs = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_CHANGE_HISTORY=output2 dsPT_R_MOT_STE=output3";
	var sOutDs = "dsPT_R_BASIC_CHANGE_HISTORY=output2 dsPT_R_MOT_STE=output3";
	var sCallBack = "fn_CallBack";
	
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

/**
 1. 함수명 : fnReport
 2. 기  능 : 출력버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
