}

//////////////////////////////////////////////////////////////////
// Memo Detail Event
//////////////////////////////////////////////////////////////////
function fnCompanyDetailButton_Click(obj)
{
	var sDtWorkFlag;

	if(obj == tbMain.tpMemo.btnCInsert)
	{
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_SUBSIDIARY.AddRow();
		dsPT_R_SUBSIDIARY.SetColumn(dsPT_R_SUBSIDIARY.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_SUBSIDIARY.SetColumn(dsPT_R_SUBSIDIARY.row, "SEQ", dsPT_R_SUBSIDIARY.rowcount);
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpMemo.btnCModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpMemo.btnCRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_SUBSIDIARY.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_SUBSIDIARY);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=SUBSIDIARY";	
		var sTid = "COMPANY_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUD";
		var sInDs = "dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u";
		var sOutDs = "dsPT_R_SUBSIDIARY=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpMemo.btnCCancel)
	{
		if (dsPT_R_SUBSIDIARY.GetRowType(dsPT_R_SUBSIDIARY.row) == "insert")
			dsPT_R_SUBSIDIARY.DeleteRow(dsPT_R_SUBSIDIARY.row);
		else
			fnRejectChange(dsPT_R_SUBSIDIARY);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitCompanyGroup(sDtWorkFlag);		
	}
	else if (obj == tbMain.tpMemo.btnCSave)
	{
	
		if( fnEmpty(dsPT_R_SUBSIDIARY.GetColumn(dsPT_R_SUBSIDIARY.row, "SUB_WRT_NUM")) ) {
			alert( "등록번호를 입력하세요" );
			return;
		}
		
		var sArgs = "workMODE=SUBSIDIARY";	
		var sTid = "COMPANY_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUD";
		var sInDs = "dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u";
		var sOutDs = "dsPT_R_SUBSIDIARY=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitCompanyGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnControlState(false, tbMain.tpMemo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(true, tbMain.tpMemo, "grdCOMPANY");
		
		tbMain.tpMemo.btnCInsert.Enable = true;
		tbMain.tpMemo.btnCModify.Enable = true;
		tbMain.tpMemo.btnCRemove.Enable = true;
		tbMain.tpMemo.btnCSave.Enable = false;
		tbMain.tpMemo.btnCCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnControlState(true, tbMain.tpMemo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(false, tbMain.tpMemo, "grdCOMPANY");
		
		tbMain.tpMemo.btnCInsert.Enable = false;
		tbMain.tpMemo.btnCModify.Enable = false;
		tbMain.tpMemo.btnCRemove.Enable = false;
		tbMain.tpMemo.btnCSave.Enable = true;
		tbMain.tpMemo.btnCCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnControlState(true, tbMain.tpMemo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(false, tbMain.tpMemo, "grdCOMPANY");
		
		tbMain.tpMemo.btnCInsert.Enable = false;
		tbMain.tpMemo.btnCModify.Enable = false;
		tbMain.tpMemo.btnCRemove.Enable = false;
		tbMain.tpMemo.btnCSave.Enable = true;
		tbMain.tpMemo.btnCCancel.Enable = true;
	}
}


//////////////////////////////////////////////////////////////////
// Memo Detail Event
//////////////////////////////////////////////////////////////////
function fnMemoButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpMemo.btnMemoInsert)
	{
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_REG_MEMO.AddRow();
		dsPT_R_REG_MEMO.SetColumn(dsPT_R_REG_MEMO.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_REG_MEMO.SetColumn(dsPT_R_REG_MEMO.row, "MEMO_SEQ", dsPT_R_REG_MEMO.rowcount);
		dsPT_R_REG_MEMO.SetColumn(dsPT_R_REG_MEMO.row, "CHGBRE_SEQ", 
											this.dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHGBRE_SEQ"));
		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpMemo.btnMemoModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitMemoGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpMemo.btnMemoRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_REG_MEMO.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_REG_MEMO);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		
		var sArgs = "workMODE=MEMO";
		var sTid = "MEMO_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUD";
		var sInDs = "dsPT_R_REG_MEMO=dsPT_R_REG_MEMO:u";
		var sOutDs = "dsPT_R_REG_MEMO=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpMemo.btnMemoCancel)
	{
		if (dsPT_R_REG_MEMO.GetRowType(dsPT_R_REG_MEMO.row) == "insert")
			dsPT_R_REG_MEMO.DeleteRow(dsPT_R_REG_MEMO.row);
		else
			fnRejectChange(dsPT_R_REG_MEMO);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitMemoGroup(sDtWorkFlag);				
	}
