function fnRefusalDetailButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpReject.btnMemoInsert)
	{
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_REFUSAL_REASON.AddRow();
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "SEQ", dsPT_R_REFUSAL_REASON.rowcount);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "CHGBRE_SEQ", 
											this.dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHGBRE_SEQ"));
		fnInitRefusalGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpReject.btnMemoModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitRefusalGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpReject.btnMemoRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_REFUSAL_REASON.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_REFUSAL_REASON);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=REFUSAL";	
		var sTid = "REFUSAL_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUD";
		var sInDs = "dsPT_R_REFUSAL_REASON=dsPT_R_REFUSAL_REASON:u";
		var sOutDs = "dsPT_R_REFUSAL_REASON=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpReject.btnMemoCancel)
	{
		if (dsPT_R_REFUSAL_REASON.GetRowType(dsPT_R_REFUSAL_REASON.row) == "insert")
			dsPT_R_REFUSAL_REASON.DeleteRow(dsPT_R_REFUSAL_REASON.row);
		else
			fnRejectChange(dsPT_R_REFUSAL_REASON);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitRefusalGroup(sDtWorkFlag);		
	}
	else if (obj == tbMain.tpReject.btnMemoSave)
	{
		var sArgs = "workMODE=REFUSAL";
		var sTid = "REFUSAL_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUD";
		var sInDs = "dsPT_R_REFUSAL_REASON=dsPT_R_REFUSAL_REASON:u";
		var sOutDs = "dsPT_R_REFUSAL_REASON=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

