		tbMain.tpReject.btnMemoModify.Enable = true;
		tbMain.tpReject.btnMemoRemove.Enable = true;
		tbMain.tpReject.btnMemoSave.Enable = false;
		tbMain.tpReject.btnMemoCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnAllControlState(true, tbMain.tpReject);
		fnControlState(false, tbMain.tpReject, "grdRefusal");
		
		tbMain.tpReject.btnMemoInsert.Enable = false;
		tbMain.tpReject.btnMemoModify.Enable = false;
		tbMain.tpReject.btnMemoRemove.Enable = false;
		tbMain.tpReject.btnMemoSave.Enable = true;
		tbMain.tpReject.btnMemoCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnAllControlState(true, tbMain.tpReject);
		fnControlState(false, tbMain.tpReject, "grdRefusal");
		
		tbMain.tpReject.btnMemoInsert.Enable = false;
		tbMain.tpReject.btnMemoModify.Enable = false;
		tbMain.tpReject.btnMemoRemove.Enable = false;
		tbMain.tpReject.btnMemoSave.Enable = true;
		tbMain.tpReject.btnMemoCancel.Enable = true;
	}
}

