function tbMain_tpReper_btnReperReport_OnClick(obj)
{
	for (var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
	{
		dsPT_R_REFFER.SetColumn(nLoop, "SEND_YN", "Y");
		dsPT_R_REFFER.SetColumn(nLoop, "CHAR_REF_YN", "2");
	}
		
	var sArgs = "workMODE=REPER";
	var sTid = "REPER_REPORT";
	var sUrl = "ICCS::cm/CmAction.do?cmd=RegMgrCCUD";
	var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER:u";
	var sOutDs = "dsPT_R_REFFER=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fnReferReport()
{
	var sViewer = "_sViewer=PersonInspect";
	var sReportname =  " _sReportname=connection.reportname=/BusinessRegist/PersonInspect.ozr";
	var sOdiNames = " _sOdiNames=odi.odinames=Q)PersonInspect";
	var	spCount = " _spCount=odi.Q)PersonInspect.pcount=1";
	var sParam = " _sParam=odi.Q)PersonInspect.args1=TMP_WRT_NUM=" + _sKEY1;
	
	var sRet = fnCreateDialog("bas::frmReport.xml", sViewer + sReportname + sOdiNames + spCount + sParam);	
	
}

//////////////////////////////////////////////////////////////////
// Master Button Click Event
//////////////////////////////////////////////////////////////////
/**

 1. 함수명 : fnMasterButton_Click
 2. 기  능 : 각 화면의 Master Button click 처리
 3. Params 
    @obj : Button object
 4. Return
    없음
*/
function fnMasterButton_Click(obj)
{
	if (obj == this.btnGetREC)
	{
		var sArgs = "_sWorkGroup=StdMgrC _sOldCode=" + RECV_NUM.Text;
		var sReturn = fnCreateDialog("cm::StdReceiptMgrD.xml", sArgs);

		if (sReturn == null) return;

		_sKEY2 = sReturn[1];
		
		// 2010.01.28. 접수일자 가져오기
		_recvDt = sReturn[4];
		
		var sArgs = "workMODE=GET_CORP scTMP_WRT_NUM=" + sReturn[3];
		sArgs += " scRECV_NUM=" +  sReturn[1];
		sArgs += " scSIDO_CODE=" + global.g_Sd_cd;
		var sTid = "GET_CORP";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrC";
		var sInDs = "";
		//var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_ENGINEER_CHANGE_ORG=output2 " ;
		var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_ENGINEER_CHANGE=output2 " ;
			sOutDs += "dsPT_R_REFFER=output3 dsPT_R_SUBSIDIARY=output4 ";
			sOutDs += "dsPT_R_DEFICIT_CODE=output5 dsMAX_SEQ=output6 dsPT_R_MEMO=output7";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	
	if (obj == this.tbMain.tbBUB.btnGetCode)
	{
		var sArgs = "_sOldCode=BLDDIV";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;

		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "OFFICE_USE_CODE", sReturn[0]);
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "OFFICE_USE_NAME", sReturn[1]);
	}
	
	if (obj == this.tbMain.tbBUB.btnGetPost)
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "ADDR_POST_NUM", sReturn[0].replace("-",""));
		dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "ADDR_ADDR", sReturn[1]);
	}
	
	if (obj == this.tbMain.tpReject.btnGetCode)
	{
		var sArgs = "_sOldCode=REGCANCEL";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;
		
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "REFS_ITEM", sReturn[0]);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "REFUSAL_DIV_NAME", sReturn[1]);
	}
	
	if (obj == this.tbMain.tpReper.btnAddr)
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "ORI", sReturn[1]);
	}
	
	if (obj == this.tbMain.tpReper.btnAddr2)
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "ADDR", rtrim(sReturn[1]));
	}
}

function fnSetDetailDataset()
{
	fnSetMasterDetail("dsPT_R_BASIC_CHANGE_HISTORY", "dsPT_R_COMPANY_MASTER");
}

function fnSetMasterDetail(dsMaster, dsDetail)
{
	var sDColName;
	var sMColName;
	var sValue;
	
	var nMCount = object(dsMaster).colcount;
	var nDCount = object(dsDetail).colcount;
	
	if(object(dsDetail).rowcount == 0)
		object(dsDetail).addrow();
	
	for(var nLoop = 0; nLoop < nDCount; nLoop++)
	{
		sDColName = object(dsDetail).GetColID(nLoop);
		for(var nIndex = 0; nIndex < nMCount; nIndex++)
		{
			sMColName = object(dsMaster).GetColID(nIndex);

			if(trim(sDColName) == trim(sMColName))
			{
				sValue = object(dsMaster).GetColumn(0, sMColName);
				object(dsDetail).SetColumn(0, sDColName, sValue);
				break;
			}
		}
	}
}


function tbMain_tpReper_btnReferCheck_OnClick(obj)
{
	if(fnEmpty(tbMain.tpReper.NAME_KOR.Text) || fnEmpty(tbMain.tpReper.SSN1.Text) || 
		fnEmpty(tbMain.tpReper.SSN2.Text))
	{
		alert('이름과 주민등록번호를 입력하세요.');
		return;
	}	
	
	dsReferCheck.DeleteAll();

	var sArgs = "workMODE=REFER_CHECK scSSN1=" + quote(tbMain.tpReper.SSN1.Text); 
		sArgs += " scSSN2=" + quote(tbMain.tpReper.SSN2.Text);
		sArgs += " scCOM_NUM=" + dsPT_R_COMPANY_MASTER.GetColumn(0, "COM_NUM");

	var sTid = "REFER_CHECK";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrC";
	var sInDs = "";
	var sOutDs = "dsReferCheck=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	
}

