{
	//조회용 다이얼로그 버튼 출력
	if (obj == this.tbMain.tpMemo.btnComDivCode)
	{		
		var sArgs = "_sOldCode=SUBSIDIARY";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		//if (sReturn == null || sReturn.length() == 0) return;
		
		//dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.row, "NOTE_CLASS_CODE", sReturn[0]);
		//dsPT_M_BEF_REPORT.SetColumn(dsPT_M_BEF_REPORT.row, "NOTE_NAME", sReturn[1]);
		
		dsPT_R_SUBSIDIARY.SetColumn(dsPT_R_SUBSIDIARY.row, "SUB_CODE", sReturn[0]);
		tbMain.tpMemo.TMPSUB_CONT = sReturn[1];
				
	}
}

function fnGovConnect(obj)
{
	if(obj == tbMain.tbBUB.btnG4C)
	{
		var sArgs = "_sURL=http://share.go.kr/";
		var sReturn = fnCreateDialog("bas::frmExplorer.xml", sArgs);
		return;
	}
	
	if(obj == tbMain.tpAmt.btnAmt)
	{
		var sMANA_NUM = dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "MANA_NUM");
		
		var sArgs = "_sURL=http://www.icfc.or.kr/ver_2/investment_lt.jsp?xecu_key=shk!@";
			sArgs += "&business_number=" + sMANA_NUM;
		
		var sReturn = fnCreateDialog("bas::frmWebInterface.xml", sArgs);
		return;
	}
}


function fnLocalGridHead_Click(obj,nCell,nX,nY,nPivotIndex)
{
	var objDs = obj.BindDataset;

	if (object(objDs).RowCount() == 0)
		return;
	 
	if (obj.GetCellProp("head", nCell, "edit") == "checkbox")
	{
		if(obj.GetCellProp("head", nCell, "text") == "1")
		{
			obj.SetCellProp("head", nCell, "text", "0");
			fnSetGridCheck(0, objDs, "DEFI_OUT");
		}
		else
		{
			obj.SetCellProp("head", nCell, "text", "1");
			fnSetGridCheck(1, objDs, "DEFI_OUT");
		}
		
		//object(objDs).ApplyChange();
	}
	else
	{
		for(var nLoop = 0; nLoop < obj.GetColCount(); nLoop++)
		{
			//alert(nLoop + '===' + nCell);
			if(nLoop == nCell) continue;
			
			obj.SetCellProp("head", nLoop, "text", replace(obj.GetCellProp("head", nLoop, "text"), "▼", ""));
			obj.SetCellProp("head", nLoop, "text", replace(obj.GetCellProp("head", nLoop, "text"), "▲", ""));
		}
		
		var sHeadName = obj.GetCellProp("head", nCell, "text");
		var sColName = obj.GetCellProp("body", nCell, "colid");
		
		if (right(sHeadName, 1) == "▼") 
		{
			object(objDs).Sort(sColName, true);
			obj.SetCellProp("head", nCell, "text", replace(sHeadName, "▼", "▲"));
		}
		else if (right(sHeadName, 1) == "▲") 
		{
			object(objDs).Sort(sColName, false);
			obj.SetCellProp("head", nCell, "text", replace(sHeadName, "▲", "▼"));
		}
		else
		{
			object(objDs).Sort(sColName, true);
			obj.SetCellProp("head", nCell, "text", sHeadName + "▼");
		}
	}
}

