function fnControlChanged(obj, strPreText, strPostText)
{
	if (obj == tbMain.tpReper.SSN1 || obj == tbMain.tpEngeneer.ENGINEER_SSN1)
	{
		if(Length(strPostText) == obj.MaxLength)
		{
			GetNextComponent(true).SetFocus();
			return;
		}
		
		return;
	}
	
	if (obj == tbMain.tpAmt.DIAGNOSTIC_DIV)
	{
	
		if (tbMain.tpAmt.DIAGNOSTIC_DIV.Value == "DIM001")
		{
		
			tbMain.tpAmt.stDiag.Value = "재무제표년도";
			tbMain.tpAmt.comDIAG.Visible = true;
			tbMain.tpAmt.COMPANY_DIAG_BAS_DT.Visible =false;
			tbMain.tpAmt.COMPANY_DIAG_BAS_DT.value = "";
		}
		else
		{
			tbMain.tpAmt.stDiag.Value = "진 단 기 준 일";
			tbMain.tpAmt.comDIAG.Visible = false;
			tbMain.tpAmt.COMPANY_DIAG_BAS_DT.Visible = true;
			tbMain.tpAmt.comDIAG.value = "";
		}
		
		fnLocalControlState();
		
		return;
				
	 } 
	 
	 
	
	
	//else if (obj == tbMain.tpAmt.DIAGNOSTIC_SUB_DIV){
		
		// if (tbMain.tpAmt.DIAGNOSTIC_SUB_DIV.Value == "DIG003")
		// {
			// tbMain.tpAmt.stDiag.Value = "재무재표년도";
			// tbMain.tpAmt.comDIAG.Visible = true;
			// tbMain.tpAmt.COMPANY_DIAG_BAS_DT.Visible =false;
		// }
		// else
		// {
			// tbMain.tpAmt.stDiag.Value = "진 단 기 준 일";
			// tbMain.tpAmt.comDIAG.Visible = false;
			// tbMain.tpAmt.COMPANY_DIAG_BAS_DT.Visible = true;
		// }
		
		// fnLocalControlState();
		
		// return;
	// }
}

