	// if( !fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHG_DT")) ) {
		// TMP_WRT_DT.Value = dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHG_DT");
