function fnLocalControlState()
{	
	if (tbMain.tpAmt.DIAGNOSTIC_DIV.Value == "DIM002")
	{
		tbMain.tpAmt.stDiag.Value = "재무제표년도";
		tbMain.tpAmt.comDIAG.Visible = true;
		tbMain.tpAmt.COMPANY_DIAG_BAS_DT.Visible =false;
		
		if (dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "COMMANA_CLASS") != "1")
			fnControlState(false, tbMain.tpAmt, "PAY_CAP");
		else
			fnControlState(true, tbMain.tpAmt, "PAY_CAP");
		
		if (dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "COMPANY_DIAG_CLASS_CODE") == "DIM002")
		{
			tbMain.tpAmt.PAY_CAP.value = "";
			fnControlState(false, tbMain.tpAmt, "PAY_CAP");
		} else {
			fnControlState(true, tbMain.tpAmt, "PAY_CAP");
		}
						
	} else {	
	
		if (tbMain.tpAmt.DIAGNOSTIC_DIV.Value == "DIG003")
		{
			tbMain.tpAmt.stDiag.Value = "재무제표년도";
			tbMain.tpAmt.comDIAG.Visible = true;
			tbMain.tpAmt.COMPANY_DIAG_BAS_DT.Visible =false;
		}
		else
		{
			tbMain.tpAmt.stDiag.Value = "진 단 기 준 일";
			tbMain.tpAmt.comDIAG.Visible = false;
			tbMain.tpAmt.COMPANY_DIAG_BAS_DT.Visible = true;
		}
	
		if (dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(dsPT_R_BASIC_CHANGE_HISTORY.row, "COMMANA_CLASS") != "1")
			fnControlState(false, tbMain.tpAmt, "PAY_CAP");
		else
			fnControlState(true, tbMain.tpAmt, "PAY_CAP");
			
		
		
	}
	
	//fnCheckMotSte();
	
}

function fnSetYear()
{
	var nIndex = 0;
	for (var nLoop = 1990; nLoop < 2050; nLoop++)
	{
		dsDIAG.AddRow();
		dsDIAG.SetColumn(nIndex, "Code", nLoop);
		dsDIAG.SetColumn(nIndex, "Value", nLoop);
		
		nIndex++;
	}
	
	var strDate = GetDate();
	strDate = Mid(strDate, 0, 4);
	
	tbMain.tpAmt.comDIAG.Value = strDate;
}

function fnSsnToGrid(obj)
{
	var ssn1 = tbMain.tpEngeneer.ENGINEER_SSN1.Text;
	var ssn2 = tbMain.tpEngeneer.ENGINEER_SSN2.Text;

	dsPT_R_ENGINEER_CHANGE.SetColumn( dsPT_R_ENGINEER_CHANGE.row, "ENGINEER_SSN", ssn1 + ssn2 );
}

/**
 * 등록상태 변경시 등록완료일자 입력 활성/비활성 처리
**/
function MOT_STE_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if( MOT_STE.Value = "STD003" ) {
		if( fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHG_DT")) ) {
			TMP_WRT_DT.Enable = true;
			TMP_WRT_DT.Value = Today();
		}
	}
	else {
		TMP_WRT_DT.Enable = false;
	}
}

