function fnList(){
	var sArgs = fnSearchInit("pSIDO_CODE,pscDATE1,pscDATE2,scRC_REGIST_NUM,pscCOM_NAME");
	sArgs += " pscMODE=T _sKEY1="+_sKEY1+" _sKEY2="+_sKEY2;
	gfnCreateNewWindow("cm::StdMgr", "cm::StdMgr.xml", sArgs);
}

// 변경사항(ABCD) 체크시 해당 컨트롤들의 Enable 변경  
function chkExtChg_OnClick(obj,strValue)
{
	// A,B,C,D 중 하나
	var extChg = right(obj.ID, 1);
	
	if( extChg == "A" ) {
		tbMain.tbBUB.ADDR_ADDR.Enable = strValue;
		tbMain.tbBUB.ADDR_DETAIL_ADDR.Enable = strValue;
		tbMain.tbBUB.ADDR_POST_NUM.Enable = strValue;
		tbMain.tbBUB.btnGetPost.Enable = strValue;
	} // if( extChg == "A" )
	
	else if( extChg == "B" ) {
		fnAllControlState(strValue, tbMain.tpAmt);
	} // if( extChg == "B" )
	
	else if( extChg == "C" ) {
		tbMain.tbBUB.REP_NAME_KOR.Enable = strValue;
		tbMain.tbBUB.Edit1.Enable = strValue;
	} // if( extChg == "C" )
	
	else if( extChg == "D" ) {
		tbMain.tbBUB.NAME.Enable = strValue;
		tbMain.tbBUB.btnG4C.Enable = strValue;
	} // if( extChg == "D" )

}
