var	_sKEY1;
var	_sKEY2;
var _sWorkFlag;
var _RecvDt;
var CHGBRE_SEQ;
 
//////////////////////////////////////////////////////////////////
// Form Load 이벤트
//////////////////////////////////////////////////////////////////
function fnForm_Loaded(obj)
{

	fnSetYear();
	
	global.gv_WorkFlag = _sWorkFlag;
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		fnInquiry();
	}
	else if(global.gv_WorkFlag == global.gv_INSERT)
	{
		global.gv_WorkFlag = global.gv_INSERT;
		fnInitButtonBar();
		 
	}
	else if(global.gv_WorkFlag == global.gv_MODIFY)
	{
		fnModify();
	}
	
	fnInitReperGroup("INQUIRY");
	fnInitEngineerGroup("INQUIRY");
	fnInitCompanyGroup("INQUIRY");
	fnInitRefusalGroup("INQUIRY");
	
	tbMain.tbInfo.CAPITAL_CHECK_YN = "P1";
	
	
	divButtonBar.Image1.text = space(6) + "공사업기준신고등록";
	

	
}

function fnForm_Activate(obj)
{
	global.g_activeFrm = this.Id;
}

//////////////////////////////////////////////////////////////////
// 공통기능
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnInitButtonBar
 2. 기  능 : 화면상단 버튼상태 설정
 3. Params 
    없음
 4. Return
    없음
*/
function fnInitButtonBar()
{
	if (global.gv_WorkFlag == global.gv_INQUIRY)
	{
	
		fnAllControlState(false, this);

		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = true;
		divButtonBar.btnRemove.enable = false;

		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, false);
		divButtonBar.btnCancel.enable = false;
		divButtonBar.btnReport.enable = true;
		divButtonBar.btnExcel.enable = false;
		divButtonBar.btnList.enable = true;

		//tbMain.tbInfo.btnGetCorp.Enable = false;
		fnLocalControlState();
	}
	else if (global.gv_WorkFlag == global.gv_INSERT)
	{
	
		
		//dsPT_R_BASIC_STATEMENT.AddRow();
		//dsPT_R_BASIC_STATEMENT.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
		fnAllControlState(false, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, false);
		divButtonBar.btnCancel.enable = false;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
		divButtonBar.btnList.enable = true;

		
				
		tbMain.tbInfo.btnGetCorp.Enable = true;
		
		
		
		fnControlState(true, this, "RECV_NUM, btnCheck");
		fnControlState(false, this,"btnGetCorp");
		//흠결코드 조회 완료 후 신규 데이터 입력처리
		dsPT_R_BASIC_STATEMENT.AddRow();
		
		this.RECV_DT.Value = Today();
		//tbMain.tpBase.PROC_LIM.Value = AddDate(Today(), 14);
		//wbjeon 2010-09-30 수정 	
		var thisToDay = Today();
		this.PROC_LIM.Value = fromDayAfter(thisToDay,7,0,"add");
		this.tbMain.tbInfo.RECV_DIV.Value = "1";
		this.tbMain.tbInfo.Radio1.Value = "N";
		this.tbMain.tbInfo.COM_DIV.Value ="1";
		this.tbMain.tbInfo.MIDAL_YN.Value = "N";
		
		this.MOT_STE.Value = "STD002";
		
		
		
	}
	else
	{
	
	//alert("3");
		fnAllControlState(true, this);
		
		divButtonBar.btnInsert.enable = false;
		divButtonBar.btnModify.enable = false;
		divButtonBar.btnRemove.enable = false;
		
		divButtonBar.btnSave.enable = fnSetMenuSaveButtonAuth(2, true);
		divButtonBar.btnCancel.enable = true;
		divButtonBar.btnReport.enable = false;
		divButtonBar.btnExcel.enable = false;
				
		fnControlState(true, this, "RECV_NUM,NAME,COI_WRT_NUM");
		fnControlState(false, this,"COI_WRT_NUM,btnCheck");
		tbMain.tbInfo.btnGetCorp.Enable = false;
		fnLocalControlState();
		
			
	}
}

/**
 1. 함수명 : fnTablesBinding
 2. 기  능 : 검색조건에 대한 검색
 3. Params 
    없음
 4. Return
    없음
*/
function fnTablesBinding()
{
	if(_sKEY1=='_sKEY2=')
	{
     var _sKEY1 = dsPT_R_BASIC_STATEMENT.GetColumn(0, "TMP_WRT_NUM");
     var _sKEY2 = dsPT_R_BASIC_STATEMENT.GetColumn(0, "RECV_NUM");
     var CHGBRE_SEQ = dsPT_R_BASIC_STATEMENT.GetColumn(0, "CHGBRE_SEQ");
     
	var sArgs = "workMODE=StdMgrC scTMP_WRT_NUM=" + quote(_sKEY1) + " scRECV_NUM=" + quote(_sKEY2) + " scCHGBRE_SEQ=" + quote(CHGBRE_SEQ) ;
	var sTid = "SEARCH";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCN";
	var sInDs = "";
    var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_STATEMENT=output1 " ;
		//sOutDs += "dsPT_R_REFFER=output8 dsPT_R_ENGINEER_CHANGE=output7 " ;
		sOutDs += "dsPT_R_REFFER=output8 dsPT_R_ENGINEER_CHANGE=output14 " ;
		//sOutDs += "dsPT_R_REG_MEMO=output9 dsPT_R_REFUSAL_REASON=output10 ";
		sOutDs += "dsPT_R_REFUSAL_REASON=output10 ";
		sOutDs += "dsPT_R_SUBSIDIARY=output11 dsPT_R_COM_DEFICIT=output12 dsPT_R_MOT_STE=output13 ";
	var sCallBack = "fn_CallBack";
		
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	break;
    }
    
	var sArgs = "workMODE=StdMgrC scTMP_WRT_NUM=" + quote(_sKEY1) + " scRECV_NUM=" + quote(_sKEY2) + " scCHGBRE_SEQ=" + quote(CHGBRE_SEQ) ;
	var sTid = "SEARCH";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCN";
	var sInDs = "";
    var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_STATEMENT=output1 " ;
		//sOutDs += "dsPT_R_REFFER=output8 dsPT_R_ENGINEER_CHANGE=output7 " ;
		sOutDs += "dsPT_R_REFFER=output8 dsPT_R_ENGINEER_CHANGE=output14 " ;
		//sOutDs += "dsPT_R_REG_MEMO=output9 dsPT_R_REFUSAL_REASON=output10 ";
		sOutDs += "dsPT_R_REFUSAL_REASON=output10 ";
		sOutDs += "dsPT_R_SUBSIDIARY=output11 dsPT_R_COM_DEFICIT=output12 dsPT_R_MOT_STE=output13 ";
	var sCallBack = "fn_CallBack";
		
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	

}

function fn_CallBack(Svcid, sErrCode, sErrMsg) 
{
	http.sync = false;
	
	
	if (sErrCode < 0) 	
	{
		if (svcid == "SEARCH")
		{
			alert('데이터 조회 중 오류가 발생하였습니다.');
			return;
		}
		if (svcid == "SAVE")
		{
			alert('데이터 저장 중 오류가 발생하였습니다.');
			return;
		}
	}
	
	
	if (svcid == "CHECK_KEY")
	{
		if (dsCheckKey.rowcount or dsCheckKey2.rowcount or dsCheckKey3.rowcount or dsCheckKey4.rowcount > 0)
		{
			alert('존재하는 민원접수번호입니다.');
			RECV_NUM.Text = "";
			RECV_NUM.SetFocus();
		}
		else
		{
		    alert('등록이 가능한 민원접수번호 입니다.');
		    		    
			dsPT_R_BASIC_STATEMENT.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
			dsPT_R_BASIC_STATEMENT.SetColumn(0, "WRT_ID", global.g_User_ID);
			
				
			
            global.gv_WorkFlag = global.gv_MODIFY;
		    fnInitButtonBar();
			fnControlState(false, this, "RECV_NUM, COI_WRT_NUM, btnCheck");
			fnControlState(true, this,"btnGetCorp");
			
			
var sReturn = fnCreateDialog("bas::CMStD.xml");
		
		if (sReturn == null) return;
        
        _sKEY1 = sReturn[2];
        
		_sKEY2 = sReturn[1];
		
		_recvDt = sReturn[4];
		
		
		var sArgs = "workMODE=GET_CORP scTMP_WRT_NUM=" + _sKEY2;
		sArgs += " scRECV_NUM=" +  RECV_NUM.Text;
		sArgs += " scSIDO_CODE=" + global.g_Sd_cd;
		sArgs += " scCOI_WRT_NUM=" + _sKEY1;
		var sTid = "GET_CORP";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCN";
		var sInDs = "";
		var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_ENGINEER_CHANGE=output2 " ;
			sOutDs += "dsPT_R_REFFER=output3 dsPT_R_SUBSIDIARY=output4 ";
			sOutDs += "dsPT_R_DEFICIT_CODE=output5 dsMAX_SEQ=output6 dsPT_R_MEMO=output7";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		}
	}
	
			if (svcid == "CHECK_KEY2")
	{
		if (dsCheckKey.rowcount or dsCheckKey2.rowcount or dsCheckKey3.rowcount or dsCheckKey4.rowcount or dsCheckKey5.rowcount or dsCheckKey6.rowcount > 0)
		{
			alert('존재하는 문서번호입니다.\n예)정보화담당관-1234-1(2011)');
			Edit0.Text = "";
			Edit0.SetFocus();
			return;
		}
	}
	// 협회 송신 큐에 넣기가 성공한 경우
	if (svcid == "KICAIfSendCUD")
	{
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "SND_STE", "O");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "SND_DT", Today());
	}
	
	if(svcid == "SEARCH")
	{
		for (var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
		{
			dsPT_R_REFFER.SetColumn(nLoop, "SEND_YN", "");
			dsPT_R_REFFER.SetColumn(nLoop, "CHAR_REF_YN", "");
		}
		
		fnLocalControlState();
		
		fnControlState(false, this, "RECV_NUM");

	    var sMOT_STE = dsPT_R_BASIC_STATEMENT.GetColumn(0, "MOT_STE");
		if(sMOT_STE == "STD003"){
	    fnAllControlState(false, this);
	    divButtonBar.btnReport.enable = true;
	    divButtonBar.btnList.enable = true;
	    }
	}
	
	if (svcid == "GET_RECV_NUM")
	{
		if (dsPT_R_REGIST_STATEMENT.GetColumn(0, "DEFI_STE") != "O")
		{
			alert('선택하신 접수건은 흠결이 완료되지 않았습니다.');
			Close();
		}
		
		fnInsert();
	}
	
	if (svcid == "REFER_CHECK")
	{
		if (dsReferCheck.rowcount > 0)
		{
			if (fnEmpty(dsReferCheck.GetColumn(0, "RETIRE_DATE")))
			{
				alert('입력하신 조회 대상자는 타 공사업체에 재직중입니다.');
				return;
			}
			else
			{
				alert('조회 대상자는 등록이 가능한 사람입니다.');
			}
		}
	}
	
	if (svcid == "GET_CORP")
	{
		
		
		var sMax_Seq = dsMAX_SEQ.GetColumn(0, "CHGBRE_SEQ");
		
	    var scRECV_NUM=dsPT_R_BASIC_STATEMENT.GetColumn(0, "RECV_NUM", RECV_NUM.Text);
		
		fnSetMasterDetail("dsPT_R_COMPANY_MASTER", "dsPT_R_BASIC_STATEMENT");
		
        var sTMP_WRT_NUM = dsPT_R_BASIC_STATEMENT.GetColumn(0, "TMP_WRT_NUM");
        //alert(sTMP_WRT_NUM);
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "RECV_NUM", scRECV_NUM);
		
		var scCOMPANY_DIAG_CLASS_CODE = dsPT_R_BASIC_STATEMENT.GetColumn(0, "COMPANY_DIAG_CLASS_CODE");
		var scDIAG_ORG_CODE = dsPT_R_BASIC_STATEMENT.GetColumn(0, "DIAG_ORG_CODE");
		
		//alert("scCOMPANY_DIAG_CLASS_CODE :"+scCOMPANY_DIAG_CLASS_CODE);
		//alert("scDIAG_ORG_CODE :"+scDIAG_ORG_CODE);
		
		
		if(scCOMPANY_DIAG_CLASS_CODE==""){
		
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "COMPANY_DIAG_CLASS_CODE", "DIM002");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "DIAG_ORG_CODE", "DIG003");
			
			tbMain.tbInfo.comDIAG.Visible = true;
			tbMain.tbInfo.COMPANY_DIAG_BAS_DT.Visible =false;
			var strdate=Mid(GetDate(),0,4);
			//alert(strdate);
			tbMain.tbInfo.comDIAG.Value = strdate;
		
		}
		else if(scCOMPANY_DIAG_CLASS_CODE=="DIM001")
		{
			tbMain.tbInfo.comDIAG.Visible = false;
			tbMain.tbInfo.COMPANY_DIAG_BAS_DT.Visible =true;
			
		}
		else
		{
		tbMain.tbInfo.comDIAG.Visible = true;
			tbMain.tbInfo.COMPANY_DIAG_BAS_DT.Visible =false;
			var strdate=Mid(GetDate(),0,4);
			//alert(strdate);
			tbMain.tbInfo.comDIAG.Value = strdate;
		}
		
		if(scDIAG_ORG_CODE=="")
		{
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "DIAG_ORG_CODE", "DIG003");
		}
				
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "REA_CAP", "");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "COMPANY_DIAG_BAS_DT", "");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "DOC_CODE", "");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "WRT_DT", "");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "DIAG_NM_WRT_NUM", "");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "DIAG_NM_NM", "");
		this.tbMain.tbInfo.CAPITAL_CHECK_YN.Value = "P1";
		//dsPT_R_BASIC_STATEMENT.SetColumn(0, "PROC_LIM", "");
		
		this.tbMain.tbInfo.REA_CAP.SetFocus();

		//dsPT_R_BASIC_CHANGE_HISTORY.AddRow();
		//dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "TMP_WRT_NUM", _sKEY1);
		//dsPT_R_BASIC_CHANGE_HISTORY.SetColumn(0, "RECV_NUM", _sKEY2);
		//dsPT_R_BASIC_STATEMENT.SetColumn(0, "CHGBRE_SEQ", sMax_Seq);
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "CHGBRE_SEQ", sMax_Seq);
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "SIDO_CODE", global.g_Sd_cd);
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "MOT_STE", "STD002");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "STA_CHG_CLASS_CODE", "R00002");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "SND_STE", "X");
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "WRT_ID", global.g_User_ID);
		
		
		dsPT_R_MOT_STE.SetColumn(0, "CHGBRE_SEQ", sMax_Seq);
		dsPT_R_MOT_STE.SetColumn(0, "TMP_WRT_NUM", sTMP_WRT_NUM);
		
		strDate = GetDate();
        strDate = Mid(strDate,0,4) + "-" + Mid(strDate,4,2) + "-" + Mid(strDate,6,2) + " " + 
                  Mid(strDate,8,2) + ":" + Mid(strDate,10,2) + ":" + Mid(strDate,12,2);
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "INS_DT", strDate);

		// 2010.01.28. 접수일자 넣기
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "REC_DT", _recvDt);

		//dsPT_R_BASIC_STATEMENT.SetColumn(0, "COMPANY_DIAG_CLASS_CODE", "P2");
		
		
		if (dsPT_R_COMPANY_MASTER.GetColumn(0, "GUBUN") == "")
		{
		
		 this.tbMain.tbInfo.RECV_DIV.Value = "1";
		//this.tbMain.tbInfo.RECV_DIV.Value = "본청";
		}
		
		if (dsPT_R_COMPANY_MASTER.GetColumn(0, "FOREIGN_YN") == "")
		{
		
		this.tbMain.tbInfo.Radio1.Value = "N";
		}
		
		if (dsPT_R_COMPANY_MASTER.GetColumn(0, "COMMANA_CLASS") == "")
		{
		
		this.tbMain.tbInfo.COM_DIV.Value = "1";
		}
		if (dsPT_R_COMPANY_MASTER.GetColumn(0, "MIDAL_YN") == "")
		{
		
		this.tbMain.tbInfo.MIDAL_YN.Value = "N";
		}
		//tbMain.tbInfo.DIAGNOSTIC_DIV.Value = "DIG003";
		//tbMain.tbInfo.CAPITAL_CHECK_YN = "P1";

		//신원조회 대상자의 초기값 설정
		for (var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
		{
			dsPT_R_REFFER.SetColumn(nLoop, "SEND_YN", "");
			dsPT_R_REFFER.SetColumn(nLoop, "CHAR_REF_YN", "");
		}

		var sGroup;
		var sCode;
		var sContent;
		var sSeq;
		
		var nIndex = 0;
		//흠결
		for(var nLoop = 0; nLoop < dsPT_R_DEFICIT_CODE.rowcount; nLoop++)
		{
			sGroup = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_GROUP");
			if(sGroup == "DRBC")
			{
				sCode = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "DEFI_CODE");
				sContent = dsPT_R_DEFICIT_CODE.GetColumn(nLoop, "CONT");
				sSeq = nIndex + 1;

				dsPT_R_COM_DEFICIT.AddRow();
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "TMP_WRT_NUM", _sKEY1);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "SEQ", sSeq);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "CHGBRE_SEQ", sMax_Seq);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "DEFI_CODE", sCode);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "DEFI_CONT", sContent);
				dsPT_R_COM_DEFICIT.SetColumn(nIndex, "DEFI_GROUP", sGroup);
				
				nIndex++;
			}
		}
		
		// 접수시 메모 가져오기
		for(var nLoop = 0; nLoop < dsPT_R_MEMO.rowcount; nLoop++)
		{
			dsPT_R_REG_MEMO.AddRow();
			dsPT_R_REG_MEMO.SetColumn(nLoop, "TMP_WRT_NUM", _sKEY1);
			dsPT_R_REG_MEMO.SetColumn(nLoop, "CHGBRE_SEQ", sMax_Seq);
			dsPT_R_REG_MEMO.SetColumn(nLoop, "MEMO_SEQ", dsPT_R_MEMO.GetColumn(nLoop, "MEMO_SEQ"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "MEMO_CONT", dsPT_R_MEMO.GetColumn(nLoop, "MEMO_CONT"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "WRT_DT", dsPT_R_MEMO.GetColumn(nLoop, "WRT_DT"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "UPD_DT", dsPT_R_MEMO.GetColumn(nLoop, "UPD_DT"));
			dsPT_R_REG_MEMO.SetColumn(nLoop, "INS_DT", dsPT_R_MEMO.GetColumn(nLoop, "INS_DT"));
			
		}
		//if (fnEmpty(tbMain.tbInfo.RECV_DIV))
		
		
		if (dsPT_R_BASIC_STATEMENT.GetColumn(dsPT_R_BASIC_STATEMENT.row, "COMMANA_CLASS") != "1")
		{
		fnControlState(false, tbMain.tbInfo, "PAY_CAP");
		}
	    else{
	    fnControlState(true, tbMain.tbInfo, "PAY_CAP");
	    }
		
		var sArgs = "workMODE=ALL";
		var sTid = "INSERT_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_COMPANY_MASTER=dsPT_R_COMPANY_MASTER:u dsPT_R_BASIC_STATEMENT=dsPT_R_BASIC_STATEMENT:u dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u dsPT_R_ENGINEER_CHANGE=dsPT_R_ENGINEER_CHANGE:u";
		var sOutDs = "dsPT_R_COMPANY_MASTER=output dsPT_R_BASIC_STATEMENT=output2 dsPT_R_SUBSIDIARY=output4 dsPT_R_ENGINEER_CHANGE=output14";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		
	}
	
	if (svcid == "INSERT_SAVE")
	{
	//fnSetMasterDetail("dsPT_R_BASIC_STATEMENT", "dsPT_R_MOT_STE");	
	 var sTUP_AOM=tbMain.tbInfo.TUP_AOM.Value;
     var scTUP_AOM=ToNumber(sTUP_AOM);
     
     //좌수
     a=323.685;
     
           //alert(scTUP_AOM/a);
           var jasu=scTUP_AOM/a;
           //alert("좌수 "+jasu);

          //this.tbMain.tbInfo.MaskEdit0.Text=round(scTUP_AOM/a,0);
          dsPT_R_BASIC_STATEMENT.SetColumn(0, "UNION_INVEST_NUM", round(scTUP_AOM/a,0));
	}
	
	if (svcid == "INSERT_SAVE2")
	{
		//global.gv_WorkFlag = global.gv_MODIFY;
		//fnInitButtonBar();
		//dsPT_R_BASIC_STATEMENT.ApplyChange();
	  //fnSetMasterDetail("dsPT_R_BASIC_CHANGE_HISTORY", "dsPT_R_BASIC_STATEMENT");
	
	}

	if (svcid == "SAVE")
	{
		alert('저장되었습니다.');
		//var _sKEY1 = dsPT_R_BASIC_STATEMENT.GetColumn(dsPT_R_BASIC_STATEMENT.row, "TMP_WRT_NUM");
		//var CHGBRE_SEQ = dsPT_R_BASIC_STATEMENT.GetColumn(dsPT_R_BASIC_STATEMENT.row, "CHGBRE_SEQ");

		fnInquiry();
		
	    var sMOT_STE = dsPT_R_BASIC_STATEMENT.GetColumn(0, "MOT_STE");
		if(sMOT_STE == "STD003"){
	    fnAllControlState(false, this);
	    divButtonBar.btnReport.enable = true;
	    divButtonBar.btnList.enable = true;
	    divButtonBar.btnInsert.enable = true; //등록완료시 신규버튼 할성화
	    }
	}
	
	if (svcid == "SAVE2")
	{
		fnInquiry();
	}

	if (svcid == "REPER_REPORT")
	{
		fnReferReport();
	}
	if (svcid == "REPER_SAVE")
	{
		alert('저장되었습니다.');
		fnInitReperGroup("INQUIRY");
	}
	if (svcid == "REPER_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitReperGroup("INQUIRY");
	}
	if (svcid == "ENGINEER_SAVE")
	{
		alert('저장되었습니다.');
		fnInitEngineerGroup("INQUIRY");
	}
	if (svcid == "ENGINEER_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitEngineerGroup("INQUIRY");
	}
	
	if (svcid == "MEMO_SAVE")
	{
		alert('저장되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	if (svcid == "MEMO_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitMemoGroup("INQUIRY");
	}
	if (svcid == "REFUSAL_SAVE")
	{
		alert('저장되었습니다.');
		fnInitRefusalGroup("INQUIRY");
	}
	if (svcid == "REFUSAL_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitRefusalGroup("INQUIRY");
	}
	if (svcid == "COMPANY_SAVE")
	{
		alert('저장되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	if (svcid == "COMPANY_DELETE")
	{
		alert('삭제되었습니다.');
		fnInitCompanyGroup("INQUIRY");
	}
	
}

//////////////////////////////////////////////////////////////////
// 툴바 버튼 이벤트 핸들러
//////////////////////////////////////////////////////////////////
function fnInquiry()
{
	global.gv_WorkFlag = global.gv_INQUIRY;
	fnInitButtonBar();
}

/**
 1. 함수명 : fnInsert
 2. 기  능 : 신규버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnInsert()
{
    
    var sKEY1 = "";
	var sKEY2 = "";
	var sWorkFlag = global.gv_INSERT;
	
	var sArgs = fnParamInit("",this);
	sArgs += "_sWorkFlag=" + sWorkFlag + " _sKEY1=" + sKEY1 + " _sKEY2=" + sKEY2;
	
    gfnCreateNewWindow("StdMgrCN", "cm::StdMgrCN.xml", sArgs);

}

function fnCancel()
{
	if(!Confirm('취소하시겠습니까?')) return;
	
	if(global.gv_WorkFlag == global.gv_INQUIRY)
	{
		//키값을 자동으로 생성할 경우 삭제하는 로직을 추가해주세요
		Close();
	}
	else
	{
		fnRejectChange(this.dsPT_R_COMPANY_MASTER);
		fnRejectChange(this.dsPT_R_BASIC_STATEMENT);
		alert('취소되었습니다.');
	}

	fnInquiry();
}

