		//	|| ToInteger(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "OFFICE_AREA")) < 20)
		if (fnEmpty(dsPT_R_BASIC_STATEMENT.GetColumn(0, "OFFICE_AREA")) 
			|| ToInteger(dsPT_R_BASIC_STATEMENT.GetColumn(0, "OFFICE_AREA")) <= 14)	
						
