			fnCheckMotSte();
			return;
		}
		
				
		//if (tbMain.tbInfo.DIAGNOSTIC_DIV.Value == "DIM001")
		//{
		/*
			if (fnEmpty(dsPT_R_BASIC_STATEMENT.GetColumn(0, "COMPANY_DIAG_ISSUE_DT")))
