						sMsg += "   등록을 완료할 수 없습니다. ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				fnCheckMotSte();
				return;		
			}
			*/
		//}
		
		 if (fnEmpty(dsPT_R_BASIC_STATEMENT.GetColumn(0, "MIDAL_YN")))
		{
			var sMsg = "충족여부가가 선택되지 않았습니다..\n\n";
						sMsg += "   등록을 완료할 수 없습니다. ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				fnCheckMotSte();
				return;		
		}
		
		if (tbMain.tbInfo.DIAGNOSTIC_DIV.Value == "DIM001")
		{
			if (fnEmpty(dsPT_R_BASIC_STATEMENT.GetColumn(0, "COMPANY_DIAG_BAS_DT")))
			{
				var sMsg = "기업진단보고서 기준일자가 입력되지 않았습니다..\n\n";
						sMsg += "   등록을 완료할 수 없습니다. ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				fnCheckMotSte();
				return;		
			}
		
			var sDay = dsPT_R_BASIC_STATEMENT.GetColumn(0, "COMPANY_DIAG_BAS_DT"); 
			
			// 2009-09-18 수정 방송통신위원회고시 제2009-16호 30일->45일변경
			//if(Today() > AddDate(sDay, 37)) 
			//if(Today() > AddDate(sDay, 45))
			if(sDay > AddDate(sDay, 45))
			{
				var sMsg = "기업진단보고서가 45일 이내에 발급되지 않았습니다.\n\n";
					sMsg += "                등록을 완료할 수 없습니다.                 ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				fnCheckMotSte();
				return;		
			}
		}
		/* else
		{
			if (fnEmpty(dsPT_R_BASIC_STATEMENT.GetColumn(0, "COMPANY_DIAG_BAS_DT2")))
			{
				var sMsg = "재무재표 발급년도가 입력되지 않았습니다..\n\n";
						sMsg += "   등록을 완료할 수 없습니다. ";
				alert(sMsg);
				MOT_STE.Value = "STD002";
				fnCheckMotSte();
				return;	
			}
		}
		*/
		for(var nLoop = 0; nLoop < dsPT_R_ENGINEER_CHANGE.rowcount; nLoop++)
		{
			if (fnEmpty(dsPT_R_ENGINEER_CHANGE.GetColumn(nLoop, "EMPL_YMD")))
				dsPT_R_ENGINEER_CHANGE.SetColumn(nLoop, "EMPL_YMD", wrtDt);
		}
		
		// 2010.01.11. 기준신고완료일자를 무조건 갱신한다.
		//if (fnEmpty(dsPT_R_COMPANY_MASTER.GetColumn(0, "BAS_STA_DT")))
		//{
		    dsPT_R_COMPANY_MASTER.SetColumn(0, "WRT_ID", global.g_User_ID);
			dsPT_R_COMPANY_MASTER.SetColumn(0, "BAS_STA_DT", wrtDt);
			dsPT_R_BASIC_STATEMENT.SetColumn(0, "BAS_STA_DT", wrtDt);
		//}
		
		//if (fnEmpty(dsPT_R_BASIC_CHANGE_HISTORY.GetColumn(0, "CHG_DT")))
			dsPT_R_BASIC_STATEMENT.SetColumn(0, "CHG_DT", wrtDt);
     /*   
        //세부변경 주석처리 하기
		// 2010.01.29. 세부변경 사항 선택
		var sArgs = " _parentType=Std _chgCode=" + dsPT_R_BASIC_STATEMENT.GetColumn(0, "EXT_CHG_CLASS_CODE");
		var sReturn = fnCreateDialog("cm::frmChgSelectDlg.xml", sArgs);
		
		if( sReturn == null )
			return;
		
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "EXT_CHG_CLASS_CODE", sReturn);
		*/
			
	}
	else {
		// 등록완료가 아닐 경우 무조건 완료일자를 없앤다.
		dsPT_R_BASIC_STATEMENT.SetColumn(0, "CHG_DT", "");
	}
	
	fnSetDetailDataset();
	
    fnSetMasterDetail("dsPT_R_BASIC_STATEMENT", "dsPT_R_COMPANY_MASTER");
    fnSetMasterDetail("dsPT_R_BASIC_STATEMENT", "dsPT_R_BASIC_CHANGE_HISTORY");
    
	if (MOT_STE.Value == "STD003")
	{
	var sArgs = "workMODE=ALL scTMP_WRT_NUM="+ _sKEY1;
	
	var sTid = "";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
	var sInDs = "dsPT_R_COMPANY_MASTER=dsPT_R_COMPANY_MASTER:u dsPT_R_BASIC_CHANGE_HISTORY=dsPT_R_BASIC_CHANGE_HISTORY:u ";
	var sOutDs = "";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	dsPT_R_BASIC_STATEMENT.SetColumn(0, "WRT_DT", TMP_WRT_DT.Value);
		
	var sArgs = "workMODE=ALL scRECV_NUM="+ _sKEY2;
	var sTid = "SAVE";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
	var sInDs = "dsPT_R_BASIC_STATEMENT=dsPT_R_BASIC_STATEMENT:u dsPT_R_MOT_STE=dsPT_R_MOT_STE:u ";
	var sOutDs = "dsPT_R_MOT_STE=output3";
	var sOutDs = "";

	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	
		if (MOT_STE.Value == "STD003")
	{
		// 등록완료시 협회송신 큐에 넣기
		dsPT_TMP_TRANS_KEY.DeleteAll();
		dsPT_TMP_TRANS_KEY.AddRow();
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "WORK_CLASS_CODE", 
					dsPT_R_BASIC_STATEMENT.GetColumn(0, "STA_CHG_CLASS_CODE"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY1", 
					dsPT_R_BASIC_STATEMENT.GetColumn(0, "TMP_WRT_NUM"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY2", 
		            dsPT_R_BASIC_STATEMENT.GetColumn(0, "RECV_NUM"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY3", 
		            dsPT_R_BASIC_STATEMENT.GetColumn(0, "SIDO_CODE"));
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "KEY4", 
		            dsPT_R_BASIC_STATEMENT.GetColumn(0, "COI_WRT_NUM"));
		if(dsPT_R_BASIC_STATEMENT.GetColumn(0, "SND_STE") == "O")
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "U");
		else
			dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "CUD_FLAG", "C");
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "SIDO_CODE", global.g_Sd_cd);
		dsPT_TMP_TRANS_KEY.SetColumn(dsPT_TMP_TRANS_KEY.row, "INS_DT", Today());
		
		http.Sync = true;
		var sArgs = "";		
		var sTid = "KICAIfSendCUD";
		var sUrl = "ICCS::sys_m/Sys_mAction.do?cmd=KICAIfSendCUD";
		var sInDs = "dsPT_TMP_TRANS_KEY=dsPT_TMP_TRANS_KEY:u";
		var sOutDs = "dsPT_TMP_TRANS_KEY=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
		http.Sync = false;
		
	}
	/*
	var sArgs = "workMODE=ALL scRECV_NUM="+ _sKEY2;
	var sTid = "SAVE";
	var sUrl = "ICCS::cm/CmAction.do?cmd=TransMgrCCUDN";
	sInDs = " dsPT_R_ENGINEER_CHANGE_TEMP=dsPT_R_ENGINEER_CHANGE_TEMP:u ";	
	var sOutDs = "";
	var sCallBack = "fn_CallBack";

	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	*/
}

/**
 1. 함수명 : fnReport
 2. 기  능 : 출력버튼 이벤트 핸들러
 3. Params 
    없음
 4. Return
