		var sViewer = "_sViewer=BasChanInnerR";
		var sReportname =  " _sReportname=connection.reportname=/BusinessRegist/BasChanInnerR.ozr";
		var sOdiNames = " _sOdiNames=odi.odinames=Q)BasChanInnerR";
