//////////////////////////////////////////////////////////////////
// Key Down 관련 이벤트
//////////////////////////////////////////////////////////////////
function fnKey_Down(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	if(nChar == "81" && bControl == true) fnTablesBinding();
	else if(nChar == "49" && bControl == true) fnInsert();
	else if(nChar == "50" && bControl == true) fnModify();
	else if(nChar == "51" && bControl == true) fnRemove();
	else if(nChar == "83" && bControl == true) fnSave();
	//else if(nChar == "55" && bControl == true) fnSave();
	else if(nChar == "80" && bControl == true) fnReport();
		else if(nChar == "13") 
	{
		var sCompnentname = GetCurrentComponent(true);
		if(sCompnentname.GetType() == "TextArea") return;
		GetNextComponent(true).SetFocus();
	}
	else if(nChar == "120") 
	{
		var nTabIndex = tbMain.TabIndex;
		
		if(nTabIndex == tbMain.TabCount - 1)
			tbMain.TabIndex = 0;
		else
			tbMain.TabIndex = nTabIndex + 1;
	}
}

//////////////////////////////////////////////////////////////////
// Validation check 이벤트
//////////////////////////////////////////////////////////////////
/**
 1. 함수명 : fnKillFocus
 2. 기  능 : Control Leave 이벤트 핸들러
 3. Params 
    없음
 4. Return
    없음
*/
function fnKillFocus(obj)
{
if (obj == tbMain.tbInfo.REP_NAME_KOR)
	{
		if( fnEmpty(tbMain.tbInfo.REP_NAME_HAN.Text) )
			tbMain.tbInfo.REP_NAME_HAN.Text = tbMain.tbInfo.REP_NAME_KOR.Text;
	}
	
	if (obj == tbMain.tpEtc.NM_KOR)
	{
		if( fnEmpty(tbMain.tpEtc.NAME_HANJA.Text) )
			tbMain.tpEtc.NAME_HANJA.Text = tbMain.tpEtc.NM_KOR.Text;
	}
	
	if (obj == tbMain.tpEtc.GUBUN)
	{
		if (dsPT_R_BASIC_STATEMENT.GetColumn(0, "COMMANA_CLASS") == 2 && 
		dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "POS_CLASS") != "P4")
		{
			alert('개인사업자의 경우 대표자만을 선택할 수 있습니다.');
			dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "POS_CLASS", "P4");
		}

		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "POS_NAME", tbMain.tpEtc.GUBUN.Text);
		
		if (dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "POS_CLASS") == "P4" ||
			dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "POS_CLASS") == "P1")
		{
			var sMasterNm = dsPT_R_BASIC_STATEMENT.GetColumn(0, "REP_NM_KOR");
			var sDetailNm = dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "NM_KOR");
			
			if (sMasterNm == sDetailNm)
			{
				dsPT_R_BASIC_STATEMENT.SetColumn(0, "REP_SSN1", 
											dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "SSN1"));
				dsPT_R_BASIC_STATEMENT.SetColumn(0, "REP_SSN2", 
											dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "SSN2"));
			}
		}
	}
}

//////////////////////////////////////////////////////////////////
// On Focus 이벤트
//////////////////////////////////////////////////////////////////

//////////////////////////////////////////////////////////////////
// Memo Detail Event
//////////////////////////////////////////////////////////////////
function fnCompanyDetailButton_Click(obj)
{
	var sDtWorkFlag;

	if(obj == tbMain.tbInfo.btnCInsert)
	{
		var _sKEY1 = dsPT_R_BASIC_STATEMENT.GetColumn(0, "TMP_WRT_NUM");

		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_SUBSIDIARY.AddRow();
		dsPT_R_SUBSIDIARY.SetColumn(dsPT_R_SUBSIDIARY.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_SUBSIDIARY.SetColumn(dsPT_R_SUBSIDIARY.row, "SEQ", dsPT_R_SUBSIDIARY.rowcount);
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tbInfo.btnCModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitCompanyGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tbInfo.btnCRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_SUBSIDIARY.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_SUBSIDIARY);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=SUBSIDIARY";	
		var sTid = "COMPANY_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u";
		var sOutDs = "dsPT_R_SUBSIDIARY=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tbInfo.btnCCancel)
	{
		if (dsPT_R_SUBSIDIARY.GetRowType(dsPT_R_SUBSIDIARY.row) == "insert")
			dsPT_R_SUBSIDIARY.DeleteRow(dsPT_R_SUBSIDIARY.row);
		else
			fnRejectChange(dsPT_R_SUBSIDIARY);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitCompanyGroup(sDtWorkFlag);		
	}
	else if (obj == tbMain.tbInfo.btnCSave)
	{
	
		if( fnEmpty(dsPT_R_SUBSIDIARY.GetColumn(dsPT_R_SUBSIDIARY.row, "SUB_WRT_NUM")) ) {
			alert( "등록번호를 입력하세요" );
			return;
		}
		
		 //겹업 중복체크 석곤 2011-06-29
    	var rtn;
    	for(i=0;i<dsPT_R_SUBSIDIARY.count;i++){
	    rtn = dsPT_R_SUBSIDIARY.SearchRow(" SUB_WRT_NUM = '" + dsPT_R_SUBSIDIARY.GetColumn(i,"SUB_WRT_NUM")+ "' &&  SUB_NAME = '" + dsPT_R_SUBSIDIARY.GetColumn(i,"SUB_NAME")+ "'",i+1,dsPT_R_SUBSIDIARY.count);
          if(rtn != -1 ){ //같은 값이 있을때
            alert("중복 입력 하였습니다");
            dsPT_R_SUBSIDIARY.DeleteRow(dsPT_R_SUBSIDIARY.row);
            
            sDtWorkFlag = global.gv_INQUIRY;
		    fnInitCompanyGroup(sDtWorkFlag);
            
            return false;
            break;
           }
         }
		
		var sArgs = "workMODE=SUBSIDIARY";	
		var sTid = "COMPANY_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_SUBSIDIARY=dsPT_R_SUBSIDIARY:u";
		var sOutDs = "dsPT_R_SUBSIDIARY=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitCompanyGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnControlState(false, tbMain.tbInfo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(true, tbMain.tbInfo, "grdCOMPANY");
		
		tbMain.tbInfo.btnCInsert.Enable = true;
		tbMain.tbInfo.btnCModify.Enable = true;
		tbMain.tbInfo.btnCRemove.Enable = true;
		tbMain.tbInfo.btnCSave.Enable = false;
		tbMain.tbInfo.btnCCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnControlState(true, tbMain.tbInfo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(false, tbMain.tbInfo, "grdCOMPANY");
		
		tbMain.tbInfo.btnCInsert.Enable = false;
		tbMain.tbInfo.btnCModify.Enable = false;
		tbMain.tbInfo.btnCRemove.Enable = false;
		tbMain.tbInfo.btnCSave.Enable = true;
		tbMain.tbInfo.btnCCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnControlState(true, tbMain.tbInfo, "TMPSUB_CONT,SUB_WRT_NUM,btnComDivCode");
		fnControlState(false, tbMain.tbInfo, "grdCOMPANY");
		
		tbMain.tbInfo.btnCInsert.Enable = false;
		tbMain.tbInfo.btnCModify.Enable = false;
		tbMain.tbInfo.btnCRemove.Enable = false;
		tbMain.tbInfo.btnCSave.Enable = true;
		tbMain.tbInfo.btnCCancel.Enable = true;
	}
}




//////////////////////////////////////////////////////////////////
// 결격사유 관련 처리
//////////////////////////////////////////////////////////////////
function fnRefusalDetailButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpEtc.btnMemoInsert)
	{
	    var _sKEY1 = dsPT_R_BASIC_STATEMENT.GetColumn(0, "TMP_WRT_NUM");
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_REFUSAL_REASON.AddRow();
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "SEQ", dsPT_R_REFUSAL_REASON.rowcount);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "CHGBRE_SEQ", 
											this.dsPT_R_BASIC_STATEMENT.GetColumn(0, "CHGBRE_SEQ"));
		fnInitRefusalGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEtc.btnMemoModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitRefusalGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEtc.btnMemoRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_REFUSAL_REASON.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_REFUSAL_REASON);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=REFUSAL";	
		var sTid = "REFUSAL_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_REFUSAL_REASON=dsPT_R_REFUSAL_REASON:u";
		var sOutDs = "dsPT_R_REFUSAL_REASON=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpEtc.btnMemoCancel)
	{
		if (dsPT_R_REFUSAL_REASON.GetRowType(dsPT_R_REFUSAL_REASON.row) == "insert")
			dsPT_R_REFUSAL_REASON.DeleteRow(dsPT_R_REFUSAL_REASON.row);
		else
			fnRejectChange(dsPT_R_REFUSAL_REASON);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitRefusalGroup(sDtWorkFlag);		
	}
	else if (obj == tbMain.tpEtc.btnMemoSave)
	{
		var sArgs = "workMODE=REFUSAL";
		var sTid = "REFUSAL_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_REFUSAL_REASON=dsPT_R_REFUSAL_REASON:u";
		var sOutDs = "dsPT_R_REFUSAL_REASON=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitRefusalGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, tbMain.tpEtc);
		fnControlState(true, tbMain.tpEtc, "grdRefusal, btnReperInsert, btnReperModify, btnReperRemove, MEMO_CONT, Edit2, Button2, Radio0, Radio2, Radio3");
		
		tbMain.tpEtc.btnMemoInsert.Enable = true;
		tbMain.tpEtc.btnMemoModify.Enable = true;
		tbMain.tpEtc.btnMemoRemove.Enable = true;
		tbMain.tpEtc.btnMemoSave.Enable = false;
		tbMain.tpEtc.btnMemoCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnAllControlState(true, tbMain.tpEtc);
		fnControlState(false, tbMain.tpEtc, "grdRefusal, btnReperCancel, btnReperSave, NM_KOR, NAME_HANJA, SSN1, SSN2, ISSUE_DATE, GUBUN, Radio1, btnAddr, ORIGIN, TextArea0, btnReferCheck, btnReper, OFFICE_AREA");

		tbMain.tpEtc.btnMemoInsert.Enable = false;
		tbMain.tpEtc.btnMemoModify.Enable = false;
		tbMain.tpEtc.btnMemoRemove.Enable = false;
		tbMain.tpEtc.btnMemoSave.Enable = true;
		tbMain.tpEtc.btnMemoCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnAllControlState(true, tbMain.tpEtc);
		fnControlState(false, tbMain.tpEtc, "grdRefusal, btnReperCancel, btnReperSave, NM_KOR, NAME_HANJA, SSN1, SSN2, ISSUE_DATE, GUBUN, Radio1, btnAddr, ORIGIN, TextArea0, btnReferCheck, btnReper, OFFICE_AREA");

		tbMain.tpEtc.btnMemoInsert.Enable = false;
		tbMain.tpEtc.btnMemoModify.Enable = false;
		tbMain.tpEtc.btnMemoRemove.Enable = false;
		tbMain.tpEtc.btnMemoSave.Enable = true;
		tbMain.tpEtc.btnMemoCancel.Enable = true;
	}
}

//////////////////////////////////////////////////////////////////
// 신원조회 관련 처리
//////////////////////////////////////////////////////////////////
function fnReperDetailButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpEtc.btnReperInsert)
	{
	    var _sKEY1 = dsPT_R_BASIC_STATEMENT.GetColumn(0, "TMP_WRT_NUM");
		http.Sync = true;
		var sArgs = "workMODE=GET_NEXT_SEQ_REFFER scTMP_WRT_NUM="+ _sKEY1;
		var sTid = "GET_NEXT_SEQ_REFFER";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCN";
		var sOutDs = "dsENGINEER_SEQ=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, "", sOutDs, sArgs, sCallBack);
		
		http.Sync = false;
		
		if( dsENGINEER_SEQ == null || dsENGINEER_SEQ.rowcount == 0 ) {
			alert( "시스템오류 : 임원을 추가할 수 없습니다." );
			return;
		}
		
		var nextSeqEngineer = dsENGINEER_SEQ.getColumn( 0, 0 );
		
		if (nextSeqEngineer ==null){
			 nextSeqEngineer = 1;
		 }
		 
		
		sDtWorkFlag = global.gv_INSERT;
		dsPT_R_REFFER.AddRow();
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "SEQ", nextSeqEngineer);
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "SI_DO_CODE", global.g_Sd_cd);
		
        var scFOREIGN_YN=dsPT_R_REFFER.GetColumn(dsPT_R_REFFER.row, "FOREIGN_YN");
		 if (scFOREIGN_YN ==null  )
		{
		this.tbMain.tpEtc.Radio1.Value = "N";		   
		}
		 
		 if (dsPT_R_REFFER.row == 0 )
		{
		this.tbMain.tpEtc.NM_KOR.Value = this.tbMain.tbInfo.REP_NAME_KOR.Value;
		this.tbMain.tpEtc.NAME_HANJA.Value = this.tbMain.tbInfo.REP_NAME_HAN.Value;
		this.tbMain.tpEtc.SSN1.Value = this.tbMain.tbInfo.REP_SSN1.Value;
		this.tbMain.tpEtc.SSN2.Value = this.tbMain.tbInfo.Edit7.Value;
		//this.tbMain.tpEtc.Edit0.Value = this.tbMain.tpInfo.ADDR_ADDR.Value;
		   
		}
		
		 if (dsPT_R_REGIST_STATEMENT.GetColumn(0, "COMMANA_CLASS") == 1 )
		{
		   this.tbMain.tpEtc.GUBUN.Value = "P1";
		   dsPT_R_REFFER.SetColumn(0, "POS_NAME", "대표이사");
		}
		else
		{
		  this.tbMain.tpEtc.GUBUN.Value = "P4";
		  dsPT_R_REFFER.SetColumn(0, "POS_NAME", "대표자");
		}
		
		fnInitReperGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEtc.btnReperModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitReperGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEtc.btnReperRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_REFFER.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_REFFER);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=REPER";	
		var sTid = "REPER_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER:u";
		var sOutDs = "dsPT_R_REFFER=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpEtc.btnReperCancel)
	{
		if (dsPT_R_REFFER.GetRowType(dsPT_R_REFFER.row) == "insert")
			dsPT_R_REFFER.DeleteRow(dsPT_R_REFFER.row);
		else
			fnRejectChange(dsPT_R_REFFER);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitReperGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEtc.btnReperSave)
	{
		var sArgs = "workMODE=REPER scTMP_WRT_NUM="+ _sKEY1;
		var sTid = "REPER_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER:u";
		var sOutDs = "dsPT_R_REFFER=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitReperGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, tbMain.tpEtc);
		fnControlState(true, tbMain.tpEtc, "btnMemoInsert, btnMemoModify, btnMemoRemove, grdReper, btnReper, MEMO_CONT, Edit2, Button2, Radio0, Radio2, Radio3");
		
		tbMain.tpEtc.btnReperInsert.Enable = true;
		tbMain.tpEtc.btnReperModify.Enable = true;
		tbMain.tpEtc.btnReperRemove.Enable = true;
		tbMain.tpEtc.btnReperSave.Enable = false;
		tbMain.tpEtc.btnReperCancel.Enable = false;
		tbMain.tpEtc.btnReper.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
	    fnAllControlState(true, tbMain.tpEtc);
		fnControlState(false, tbMain.tpEtc, "btnMemoCancel, btnMemoSave, grdReper, btnReper");

		
		tbMain.tpEtc.btnReperInsert.Enable = false;
		tbMain.tpEtc.btnReperModify.Enable = false;
		tbMain.tpEtc.btnReperRemove.Enable = false;
		tbMain.tpEtc.btnReperSave.Enable = true;
		tbMain.tpEtc.btnReperCancel.Enable = true;
		tbMain.tpEtc.btnReper.Enable =  true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnAllControlState(true, tbMain.tpEtc);
		fnControlState(false, tbMain.tpEtc, "btnMemoCancel, btnMemoSave, grdReper, btnReper");

		tbMain.tpEtc.btnReperInsert.Enable = false;
		tbMain.tpEtc.btnReperModify.Enable = false;
		tbMain.tpEtc.btnReperRemove.Enable = false;
		tbMain.tpEtc.btnReperSave.Enable = true;
		tbMain.tpEtc.btnReperCancel.Enable = true;
		tbMain.tpEtc.btnReper.Enable =  true;
	}
}



