function fnEngineerButton_Click(obj)
{
	var sDtWorkFlag;
	
	if(obj == tbMain.tpEngeneer.btnEngInsert)
	{
	    var _sKEY1 = dsPT_R_BASIC_STATEMENT.GetColumn(0, "TMP_WRT_NUM");
	
		http.Sync = true;
		var sArgs = "workMODE=GET_NEXT_SEQ_ENGINEER scTMP_WRT_NUM="+ _sKEY1;
		var sTid = "GET_NEXT_SEQ_ENGINEER";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCN";
		var sOutDs = "dsENGINEER_SEQ=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, "", sOutDs, sArgs, sCallBack);
		
		http.Sync = false;
		
		if( dsENGINEER_SEQ == null || dsENGINEER_SEQ.rowcount == 0 ) {
			alert( "시스템오류 : 기술자를 추가할 수 없습니다." );
			return;
		}
		
		var nextSeqEngineer = dsENGINEER_SEQ.getColumn( 0, 0 );
		
		sDtWorkFlag = global.gv_INSERT;

		dsPT_R_ENGINEER_CHANGE.AddRow();
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "TMP_WRT_NUM", _sKEY1);
		dsPT_R_ENGINEER_CHANGE.SetColumn(dsPT_R_ENGINEER_CHANGE.row, "SEQ", nextSeqEngineer );
		dsPT_R_ENGINEER_CHANGE.SetColumn(this.dsPT_R_ENGINEER_CHANGE.row, "ENG_PROC", "S");
		dsPT_R_ENGINEER_CHANGE.SetColumn(this.dsPT_R_ENGINEER_CHANGE.row, "WRT_ID", global.g_User_ID);
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEngeneer.btnEngModify)
	{
		sDtWorkFlag = global.gv_MODIFY;
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEngeneer.btnEngRemove)
	{
		sDtWorkFlag = global.gv_INQUIRY;
		
		dsPT_R_ENGINEER_CHANGE.ApplyChange();
		var nCount = fnGridRowRemove(dsPT_R_ENGINEER_CHANGE);
		
		if(nCount == 0)
		{
			alert('삭제할 데이터를 선택하세요.');
			return;
		}
		var sArgs = "workMODE=ENGINEER";	
		var sTid = "ENGINEER_DELETE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_ENGINEER_CHANGE=dsPT_R_ENGINEER_CHANGE:u";
		var sOutDs = "dsPT_R_ENGINEER_CHANGE=output";
		var sCallBack = "fn_CallBack";

		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
	else if (obj == tbMain.tpEngeneer.btnEngCancel)
	{
		if (dsPT_R_ENGINEER_CHANGE.GetRowType(dsPT_R_ENGINEER_CHANGE.row) == "insert")
			dsPT_R_ENGINEER_CHANGE.DeleteRow(dsPT_R_ENGINEER_CHANGE.row);
		else
			fnRejectChange(dsPT_R_ENGINEER_CHANGE);
		
		sDtWorkFlag = global.gv_INQUIRY;
		fnInitEngineerGroup(sDtWorkFlag);
	}
	else if (obj == tbMain.tpEngeneer.btnEngSave)
	{
	
		if(fnEmpty(tbMain.tpEngeneer.ENGINEER_SSN1.Text) || fnEmpty(tbMain.tpEngeneer.ENGINEER_SSN2.Text) )
	{
		alert('주민등록번호를 입력하세요.');
		tbMain.tpEngeneer.ENGINEER_SSN1.SetFocus();
		return;
	}	
		var sArgs = "workMODE=ENGINEER scTMP_WRT_NUM=" + quote(_sKEY1);
		var sTid = "ENGINEER_SAVE";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCCUDN";
		var sInDs = "dsPT_R_ENGINEER_CHANGE=dsPT_R_ENGINEER_CHANGE:u";
		var sOutDs = "dsPT_R_ENGINEER_CHANGE=output";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	}
}

function fnInitEngineerGroup(sDtWorkFlag)
{
	if(sDtWorkFlag == global.gv_INQUIRY)
	{
		fnAllControlState(false, tbMain.tpEngeneer);
		fnControlState(true, tbMain.tpEngeneer, "grdEngineer,grdEngCheck");

		tbMain.tpEngeneer.btnEngInsert.Enable = true;
		tbMain.tpEngeneer.btnEngModify.Enable = true;
		tbMain.tpEngeneer.btnEngRemove.Enable = true;
		tbMain.tpEngeneer.btnEngSave.Enable = false;
		tbMain.tpEngeneer.btnEngCancel.Enable = false;
	}
	else if(sDtWorkFlag == global.gv_INSERT)
	{
		fnAllControlState(false, tbMain.tpEngeneer);
		fnAllControlState(true, tbMain.tpEngeneer);
		
		tbMain.tpEngeneer.btnEngInsert.Enable = false;
		tbMain.tpEngeneer.btnEngModify.Enable = false;
		tbMain.tpEngeneer.btnEngRemove.Enable = false;
		tbMain.tpEngeneer.btnEngSave.Enable = true;
		tbMain.tpEngeneer.btnEngCancel.Enable = true;
	}
	else if(sDtWorkFlag == global.gv_MODIFY)
	{
		fnAllControlState(false, tbMain.tpEngeneer);
		//fnControlState(true, tbMain.tpEngeneer, "ENGINEER_SSN1,ENGINEER_SSN2,ENGINEER_NM,btnEngineer,REMARK,EMPL_YMD,RET_YMD");
		fnAllControlState(true, tbMain.tpEngeneer);
		
		tbMain.tpEngeneer.btnEngInsert.Enable = false;
		tbMain.tpEngeneer.btnEngModify.Enable = false;
		tbMain.tpEngeneer.btnEngRemove.Enable = false;
		tbMain.tpEngeneer.btnEngSave.Enable = true;
		tbMain.tpEngeneer.btnEngCancel.Enable = true;
	}
}

function tbMain_tpReper_btnReperReport_OnClick(obj)
{
	for (var nLoop = 0; nLoop < dsPT_R_REFFER.rowcount; nLoop++)
	{
		dsPT_R_REFFER.SetColumn(nLoop, "SEND_YN", "Y");
		dsPT_R_REFFER.SetColumn(nLoop, "CHAR_REF_YN", "2");
	}
		
	var sArgs = "workMODE=REPER";
	var sTid = "REPER_REPORT";
	var sUrl = "ICCS::cm/CmAction.do?cmd=RegMgrCCUDN";
	var sInDs = "dsPT_R_REFFER=dsPT_R_REFFER:u";
	var sOutDs = "dsPT_R_REFFER=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
}

function fnReferReport()
{
	var sViewer = "_sViewer=PersonInspect";
	var sReportname =  " _sReportname=connection.reportname=/BusinessRegist/PersonInspect.ozr";
	var sOdiNames = " _sOdiNames=odi.odinames=Q)PersonInspect";
	var	spCount = " _spCount=odi.Q)PersonInspect.pcount=1";
	var sParam = " _sParam=odi.Q)PersonInspect.args1=TMP_WRT_NUM=" + _sKEY1;
	
	var sRet = fnCreateDialog("bas::frmReport.xml", sViewer + sReportname + sOdiNames + spCount + sParam);	
	
}

//////////////////////////////////////////////////////////////////
// Master Button Click Event
//////////////////////////////////////////////////////////////////
/**

 1. 함수명 : fnMasterButton_Click
 2. 기  능 : 각 화면의 Master Button click 처리
 3. Params 
    @obj : Button object
 4. Return
    없음
*/
function fnMasterButton_Click(obj)
{
	
	if (obj == this.tbMain.tpEtc.Button2)
	{
		var sArgs = "_sOldCode=BLDDIV";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;

		dsPT_R_BASIC_STATEMENT.SetColumn(dsPT_R_BASIC_STATEMENT.row, "OFFICE_USE_CODE", sReturn[0]);
		dsPT_R_BASIC_STATEMENT.SetColumn(dsPT_R_BASIC_STATEMENT.row, "OFFICE_USE_NAME", sReturn[1]);
	}
	
	if (obj == this.tbMain.tbInfo.btnGetPost)
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		dsPT_R_BASIC_STATEMENT.SetColumn(dsPT_R_BASIC_STATEMENT.row, "ADDR_POST_NUM", sReturn[0].replace("-",""));
		dsPT_R_BASIC_STATEMENT.SetColumn(dsPT_R_BASIC_STATEMENT.row, "ADDR_ADDR", sReturn[1]);
		dsPT_R_BASIC_STATEMENT.SetColumn(dsPT_R_BASIC_STATEMENT.row, "ADDR_DETAIL_ADDR", sReturn[2]);
		//tbMain.tbInfo.ADDR_DETAIL_ADDR.Text = "";
		tbMain.tbInfo.ADDR_DETAIL_ADDR.SetFocus();
	}
	
	if (obj == this.tbMain.tpEtc.btnGetCode)
	{
		var sArgs = "_sOldCode=REGCANCEL";
		var sReturn = fnCreateDialog("bas::CommonCodeD.xml", sArgs);
		
		if (sReturn == null || sReturn.length() == 0) return;
		
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "REFS_ITEM", sReturn[0]);
		dsPT_R_REFUSAL_REASON.SetColumn(dsPT_R_REFUSAL_REASON.row, "REFUSAL_DIV_NAME", sReturn[1]);
	}
	
	if (obj == this.tbMain.tpEtc.btnAddr)
	{
		var sReturn = fnCreateDialog("bas::PostStD.xml");
		dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "ORI", sReturn[3]);
	}
	
	 if (obj == this.tbMain.tpEtc.btnAddr2)
	 {
		 var sReturn = fnCreateDialog("bas::PostStD.xml");
		 dsPT_R_REFFER.SetColumn(dsPT_R_REFFER.row, "ADDR", rtrim(sReturn[3]));
	 }
	
		if (obj == this.tbMain.tbInfo.btnGetCorp)
	{	
		//var sArgs = "_sWorkGroup=StdMgrC _sOldCode=" + RECV_NUM.Text;
		var sReturn = fnCreateDialog("bas::CMStD.xml");

		if (sReturn == null) return;

        _sKEY1 = sReturn[2];

		_sKEY2 = sReturn[1];

		_recvDt = sReturn[4];
		
		var sArgs = "workMODE=GET_CORP scTMP_WRT_NUM=" + _sKEY2;
		sArgs += " scRECV_NUM=" +  RECV_NUM.Text;
		sArgs += " scSIDO_CODE=" + global.g_Sd_cd;
		sArgs += " scCOI_WRT_NUM=" + _sKEY1;
		var sTid = "GET_CORP";
		var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCN";
		var sInDs = "";
		var sOutDs  = "dsPT_R_COMPANY_MASTER=output dsPT_R_ENGINEER_CHANGE=output2 " ;
			sOutDs += "dsPT_R_REFFER=output3 dsPT_R_SUBSIDIARY=output4 ";
			sOutDs += "dsPT_R_DEFICIT_CODE=output5 dsMAX_SEQ=output6 dsPT_R_MEMO=output7";
		var sCallBack = "fn_CallBack";
		
		Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
			
	}
}

function fnSetDetailDataset()
{ 
	fnSetMasterDetail("dsPT_R_BASIC_STATEMENT", "dsPT_R_COMPANY_MASTER");
	fnSetMasterDetail("dsPT_R_BASIC_STATEMENT", "dsPT_R_BASIC_CHANGE_HISTORY");
	
	/*
	for (var eng=0; eng < dsPT_R_ENGINEER_CHANGE.count; eng++)
    {
	fnSetMasterDetailMulti("dsPT_R_ENGINEER_CHANGE_TEMP", "dsPT_R_ENGINEER_CHANGE",eng);
	}
	*/
	
}

function fnSetMasterDetail(dsMaster, dsDetail)
{
	var sDColName;
	var sMColName;
	var sValue;
	
	var nMCount = object(dsMaster).colcount;
	var nDCount = object(dsDetail).colcount;
	
	if(object(dsDetail).rowcount == 0)
		object(dsDetail).addrow();
	
	for(var nLoop = 0; nLoop < nDCount; nLoop++)
	{
		sDColName = object(dsDetail).GetColID(nLoop);
		for(var nIndex = 0; nIndex < nMCount; nIndex++)
		{
			sMColName = object(dsMaster).GetColID(nIndex);

			if(trim(sDColName) == trim(sMColName))
			{
				sValue = object(dsMaster).GetColumn(0, sMColName);
				object(dsDetail).SetColumn(0, sDColName, sValue);
				break;
			}
		}
	}
}


function tbMain_tpReper_btnReferCheck_OnClick(obj)
{
	if(fnEmpty(tbMain.tpEtc.NM_KOR.Text) || fnEmpty(tbMain.tpEtc.SSN1.Text) || 
		fnEmpty(tbMain.tpEtc.SSN2.Text))
	{
		alert('이름과 주민등록번호를 입력하세요.');
		return;
	}	
	
	dsReferCheck.DeleteAll();

	var sArgs = "workMODE=REFER_CHECK scSSN1=" + quote(tbMain.tpEtc.SSN1.Text); 
		sArgs += " scSSN2=" + quote(tbMain.tpEtc.SSN2.Text);
		sArgs += " scCOM_NUM=" + dsPT_R_BASIC_STATEMENT.GetColumn(0, "COM_NUM");

	var sTid = "REFER_CHECK";
	var sUrl = "ICCS::cm/CmAction.do?cmd=StdMgrCN";
	var sInDs = "";
	var sOutDs = "dsReferCheck=output";
	var sCallBack = "fn_CallBack";
	
	Transaction(sTid, sUrl, sInDs, sOutDs, sArgs, sCallBack);
	
}

