/**
 * 사업자구분 변경시 '법인사업자'일 경우 '납입자본금' 텍스박스 컨트롤
**/
function tbMain_tbBUB_Radio0_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	
	if (dsPT_R_BASIC_STATEMENT.GetColumn(dsPT_R_BASIC_STATEMENT.row, "COMMANA_CLASS") != "1")
		{
		fnControlState(false, tbMain.tbInfo, "PAY_CAP");
		}
	else{
	fnControlState(true, tbMain.tbInfo, "PAY_CAP");
	}
	
	
		if (dsPT_R_BASIC_STATEMENT.GetColumn(dsPT_R_BASIC_STATEMENT.row, "COMMANA_CLASS") = "2")
	{
		fnControlState(false, tbMain.tbInfo, "COM_NUM");
	}
	else
	{
		fnControlState(true, tbMain.tbInfo, "COM_NUM");

	}
}

