function fnSetYear()
{
	var nIndex = 0;
	for (var nLoop = 1990; nLoop < 2050; nLoop++)
	{
		dsDIAG.AddRow();
		dsDIAG.SetColumn(nIndex, "Code", nLoop);
		dsDIAG.SetColumn(nIndex, "Value", nLoop);
		
		nIndex++;
	}
	
	var strDate = GetDate();
	strDate = Mid(strDate, 0, 4);
	
	tbMain.tbInfo.comDIAG.Value = strDate;
}

function fnSsnToGrid(obj)
{
	var ssn1 = tbMain.tpEngeneer.ENGINEER_SSN1.Text;
	var ssn2 = tbMain.tpEngeneer.ENGINEER_SSN2.Text;

	dsPT_R_ENGINEER_CHANGE.SetColumn( dsPT_R_ENGINEER_CHANGE.row, "ENGINEER_SSN", ssn1 + ssn2 );
}

/**
 * 등록상태 변경시 등록완료일자 입력 활성/비활성 처리
**/
function MOT_STE_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if( MOT_STE.Value = "STD003" ) {
		MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;
		if( fnEmpty(dsPT_R_BASIC_STATEMENT.GetColumn(0, "CHG_DT")) ) {
			TMP_WRT_DT.Value = Today();
		}
	}
	else {
		MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;
		TMP_WRT_DT.Value = "";
	}
}

function fnCheckMotSte() {
	if( MOT_STE.Value = "STD003" ) {
		MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;
		if( !fnEmpty(dsPT_R_BASIC_STATEMENT.GetColumn(0, "CHG_DT")) )
			TMP_WRT_DT.Value = dsPT_R_BASIC_STATEMENT.GetColumn(0, "CHG_DT");
		else
			TMP_WRT_DT.Value = "";
	}
	else {
		MOT_STE.Enable = true;
		TMP_WRT_DT.Enable = true;
		TMP_WRT_DT.Value = "";
	}
}

